import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

export default function Visualizer({ path = [], distance = 0, algorithm, all = [] }) {
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => setDimensions({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const { width, height } = dimensions;

  const cityCoords = {
    A: { x: 5, y: 80 },
    B: { x: 15, y: 70 },
    C: { x: 10, y: 50 },
    D: { x: 20, y: 40 },
    E: { x: 25, y: 75 },
    F: { x: 30, y: 65 },
    G: { x: 25, y: 40 },
    H: { x: 35, y: 50 },
    I: { x: 45, y: 40 },
    J: { x: 40, y: 75 },
    K: { x: 50, y: 65 },
    L: { x: 40, y: 30 },
    M: { x: 50, y: 45 },
    N: { x: 60, y: 35 },
    O: { x: 55, y: 75 },
    P: { x: 65, y: 60 },
    Q: { x: 70, y: 45 },
  };

  const scaled = {};
  for (const c in cityCoords) {
    scaled[c] = { x: (cityCoords[c].x / 100) * width, y: (cityCoords[c].y / 100) * height };
  }

  const drawPath = (p, color, delay = 0) => {
    const segs = [];
    for (let i = 0; i < p.length - 1; i++) {
      const start = scaled[p[i]];
      const end = scaled[p[i + 1]];
      if (!start || !end) continue;
      const dx = end.x - start.x;
      const dy = end.y - start.y;
      const len = Math.hypot(dx, dy);
      const angle = Math.atan2(dy, dx);
      segs.push(
        <motion.div
          key={`${color}-${i}`}
          className={`absolute h-[3px] bg-${color} shadow-[0_0_15px]`}
          initial={{ scaleX: 0 }}
          animate={{ scaleX: 1 }}
          transition={{ duration: 1, delay: delay + i * 0.3 }}
          style={{
            left: start.x,
            top: start.y,
            width: len,
            transformOrigin: "0% 50%",
            rotate: `${angle}rad`,
          }}
        />
      );
    }
    return segs;
  };

  return (
    <div
      className="relative w-full h-screen overflow-hidden"
      style={{
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* City Markers */}
      {Object.entries(scaled).map(([c, pos]) => (
        <div
          key={c}
          className="absolute w-6 h-6 bg-blue-400 rounded-full flex items-center justify-center text-sm text-white font-semibold"
          style={{ left: pos.x, top: pos.y, transform: "translate(-50%, -50%)" }}
        >
          {c}
        </div>
      ))}

      {/* Route Lines */}
      {algorithm === "compare"
        ? all.flatMap((algo, idx) => drawPath(algo.data.path, algo.color, idx * 0.5))
        : drawPath(path, "yellow-400")}

      {/* Legend */}
      {algorithm === "compare" && (
        <div className="absolute bottom-6 left-6 bg-slate-900/70 p-4 rounded-xl text-sm">
          <h3 className="font-semibold mb-2 text-yellow-300">Algorithm Legend</h3>
          {all.map((a, i) => (
            <p key={i}>
              <span className={`text-${a.color}`}>‚óè</span> {a.name}
            </p>
          ))}
        </div>
      )}
    </div>
  );
}
