import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import Visualizer from "./Visualizer";
import StatsCard from "./StatsCard";
import {
  dijkstra,
  bellmanFord,
  floydWarshall,
  johnson,
  aStar,
} from "./algorithms";

function App() {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [algorithm, setAlgorithm] = useState("Dijkstra");
  const [result, setResult] = useState(null);
  const [showMap, setShowMap] = useState(false);

  // Graph Data
  const graph = {
    A: { B: 4, C: 2 },
    B: { A: 4, C: 5, D: 10 },
    C: { A: 2, B: 5, D: 3, E: 4 },
    D: { B: 10, C: 3, E: 6, F: 2 },
    E: { C: 4, D: 6, F: 1 },
    F: { D: 2, E: 1 },
  };

  // Main function to find path
  const findShortestPath = () => {
    if (!source || !destination) {
      alert("Please enter both source and destination cities (A–F).");
      return;
    }

    let resultData;

    switch (algorithm) {
      case "Dijkstra":
        resultData = dijkstra(graph, source, destination);
        break;
      case "Bellman-Ford":
        resultData = bellmanFord(graph, source, destination);
        break;
      case "Floyd-Warshall":
        resultData = floydWarshall(graph, source, destination);
        break;
      case "Johnson":
        resultData = johnson(graph, source, destination);
        break;
      case "A*":
        // Simple mock heuristic — can be customized later
        const heuristic = { A: 5, B: 4, C: 3, D: 2, E: 1, F: 0 };
        resultData = aStar(graph, source, destination, heuristic);
        break;
      default:
        resultData = dijkstra(graph, source, destination);
    }

    setResult(resultData);
    setShowMap(true);
  };

  const resetView = () => {
    setShowMap(false);
    setResult(null);
    setSource("");
    setDestination("");
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-slate-900 to-blue-950 text-white overflow-hidden">
      <AnimatePresence mode="wait">
        {!showMap ? (
          // ===== FORM SCREEN =====
          <motion.div
            key="form"
            className="text-center space-y-6 p-10 bg-slate-800/60 rounded-2xl shadow-2xl border border-slate-700 backdrop-blur-sm"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.6 }}
          >
            <h1 className="text-4xl font-bold mb-6 text-blue-400">
              ✈️ Airplane Route Optimizer
            </h1>

            {/* Input Fields */}
            <div className="flex flex-col md:flex-row justify-center gap-4">
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Enter Source City (A-F)"
                value={source}
                onChange={(e) => setSource(e.target.value.toUpperCase())}
              />
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Enter Destination City (A-F)"
                value={destination}
                onChange={(e) => setDestination(e.target.value.toUpperCase())}
              />
            </div>

            {/* Algorithm Selector */}
            <div className="mt-4">
              <select
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
                className="p-3 bg-slate-700 text-white rounded-xl border border-slate-600"
              >
                <option value="Dijkstra">Dijkstra</option>
                <option value="Bellman-Ford">Bellman-Ford</option>
                <option value="Floyd-Warshall">Floyd-Warshall</option>
                <option value="Johnson">Johnson’s Algorithm</option>
                <option value="A*">A* (A-Star)</option>
              </select>
            </div>

            {/* Find Button */}
            <button
              onClick={findShortestPath}
              className="bg-blue-500 hover:bg-blue-600 px-8 py-3 rounded-xl text-lg font-semibold transition transform hover:scale-105 shadow-lg shadow-blue-700/50 mt-4"
            >
              Find Route
            </button>

            {result && <StatsCard result={result} />}
          </motion.div>
        ) : (
          // ===== MAP SCREEN =====
          <motion.div
            key="map"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
            className="relative w-full h-screen"
          >
            {/* Visual Map */}
            <Visualizer path={result?.path || []} distance={result?.distance} />

            {/* Route Summary Card */}
            <div className="absolute top-6 left-6 bg-slate-800/70 border border-slate-700 rounded-xl p-4 shadow-lg text-sm backdrop-blur-md">
              <h2 className="font-semibold text-blue-400 mb-2">Route Summary</h2>
              <p>Algorithm: {algorithm}</p>
              <p>Path: {result?.path.join(" → ")}</p>
              <p>Distance: {result?.distance}</p>
            </div>

            {/* Back Button */}
            <button
              onClick={resetView}
              className="absolute top-6 right-6 bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-xl shadow-lg"
            >
              ← Go Back
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
