import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

/**
 * Visualizer props:
 *  - path: array of node ids (single-mode) e.g. ["A","C","H","M","Q"]
 *  - distance: number
 *  - algorithm: string (e.g. "dijkstra" or "compare")
 *  - all: array for compare mode: [{ name, color, data: { path, distance } }, ...]
 */

const COLOR_HEX = {
  blue: "#60A5FA",
  green: "#34D399",
  red: "#F87171",
  yellow: "#FACC15",
  purple: "#C084FC",
  gray: "#94A3B8",
};

export default function Visualizer({ path = [], distance = 0, algorithm = "", all = [] }) {
  const [size, setSize] = useState({ width: 1200, height: 700 });

  useEffect(() => {
    const onResize = () => setSize({ width: window.innerWidth, height: window.innerHeight });
    onResize();
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  const { width, height } = size;

  // --- Coordinates (percentages 0..100) tuned to land on typical map.png
  const cityCoords = {
    A: { x: 10, y: 52 }, // West US
    B: { x: 18, y: 50 }, // US Gulf
    C: { x: 28, y: 44 }, // East US
    D: { x: 30, y: 72 }, // S. America
    E: { x: 45, y: 48 }, // Europe/West Africa
    F: { x: 50, y: 35 }, // UK/EU
    G: { x: 48, y: 62 }, // Central Africa
    H: { x: 60, y: 50 }, // Middle East / N Africa
    I: { x: 66, y: 36 }, // Central Asia
    J: { x: 70, y: 38 }, // Pakistan/west India border
    // India cluster
    K: { x: 74, y: 40 }, // North-West India
    L: { x: 76, y: 48 }, // Central India
    M: { x: 78, y: 57 }, // South India
    N: { x: 82, y: 34 }, // Bangladesh/NE India
    O: { x: 85, y: 60 }, // SE Asia
    P: { x: 90, y: 50 }, // China region
    Q: { x: 94, y: 44 }, // Japan/East
  };

  // Convert percent -> pixels
  const scaled = {};
  for (const k in cityCoords) {
    scaled[k] = {
      x: (cityCoords[k].x / 100) * width,
      y: (cityCoords[k].y / 100) * height,
    };
  }

  // Utility: safe-get path array
  const safePath = Array.isArray(path) ? path.filter(Boolean) : [];

  // Draw a single segment (div) from node 'from' to node 'to' with colorHex
  function Segment({ from, to, colorHex, delay = 0 }) {
    const a = scaled[from];
    const b = scaled[to];
    if (!a || !b) return null;

    const dx = b.x - a.x;
    const dy = b.y - a.y;
    const len = Math.hypot(dx, dy);
    const angle = Math.atan2(dy, dx); // radians

    return (
      <motion.div
        key={`seg-${from}-${to}-${colorHex}-${delay}`}
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ duration: 0.9, delay }}
        style={{
          position: "absolute",
          left: a.x,
          top: a.y,
          width: Math.max(1, len),
          height: 3,
          transformOrigin: "0% 50%",
          transform: `rotate(${angle}rad)`,
          background: colorHex,
          boxShadow: `0 0 10px ${colorHex}66`,
          zIndex: 10,
        }}
      />
    );
  }

  // Render whole path (array of node ids) with color and base delay
  function renderPathNodes(p, colorHex = COLOR_HEX.yellow, delayBase = 0) {
    if (!Array.isArray(p) || p.length < 2) return null;
    const segs = [];
    for (let i = 0; i < p.length - 1; i++) {
      segs.push(<Segment key={`seg-${i}-${p[i]}-${p[i + 1]}`} from={p[i]} to={p[i + 1]} colorHex={colorHex} delay={delayBase + i * 0.16} />);
    }
    return segs;
  }

  // Precompute single-mode segments for plane animation
  const singleSegments = [];
  if (safePath.length > 1) {
    for (let i = 0; i < safePath.length - 1; i++) {
      const a = scaled[safePath[i]];
      const b = scaled[safePath[i + 1]];
      if (a && b) singleSegments.push([a, b]);
    }
  }

  // Plane keyframes (x,y arrays in pixels)
  const planeXs = [];
  const planeYs = [];
  if (singleSegments.length > 0) {
    // start at first segment start, then every segment end
    planeXs.push(singleSegments[0][0].x);
    planeYs.push(singleSegments[0][0].y);
    singleSegments.forEach((s) => {
      planeXs.push(s[1].x);
      planeYs.push(s[1].y);
    });
  }

  // Helper to map algorithm color token -> hex
  function algColorHex(name) {
    if (!name) return COLOR_HEX.yellow;
    const key = String(name).toLowerCase();
    if (key.includes("dijkstra")) return COLOR_HEX.blue;
    if (key.includes("bellman")) return COLOR_HEX.green;
    if (key.includes("floyd")) return COLOR_HEX.red;
    if (key.includes("a*") || key === "astar" || key.includes("a-star")) return COLOR_HEX.purple;
    if (key.includes("johnson")) return COLOR_HEX.purple;
    return COLOR_HEX.yellow;
  }

  // times array for plane animation evenly spaced
  const times = planeXs.length > 1 ? planeXs.map((_, i) => i / (planeXs.length - 1)) : [0];

  // Safety: if path empty, show nothing
  const hasSinglePath = safePath.length > 0;
  const hasCompare = algorithm === "compare" && Array.isArray(all) && all.length > 0;

  return (
    <div
      style={{
        width: "100%",
        height: "100vh",
        position: "relative",
        overflow: "hidden",
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* City markers */}
      {Object.entries(scaled).map(([node, pos]) => (
        <div
          key={`node-${node}`}
          style={{
            position: "absolute",
            left: pos.x,
            top: pos.y,
            transform: "translate(-50%,-50%)",
            zIndex: 40,
            pointerEvents: "auto",
          }}
        >
          <div
            style={{
              width: 30,
              height: 30,
              borderRadius: 999,
              background: "#2563eb",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "white",
              fontWeight: 700,
              fontSize: 12,
              boxShadow: "0 8px 22px rgba(37,99,235,0.18)",
            }}
          >
            {node}
          </div>
        </div>
      ))}

      {/* Draw lines */}
      {hasCompare
        ? // draw every algorithm's route in compare mode
          all.map((algo, idx) => {
            const colorHex = algo.colorHex || algColorHex(algo.name) || COLOR_HEX.yellow;
            const p = algo.data?.path || [];
            return (
              <React.Fragment key={`cmp-${idx}`}>
                {renderPathNodes(p, colorHex, idx * 0.22)}
              </React.Fragment>
            );
          })
        : // single algorithm mode
          renderPathNodes(safePath, algColorHex(algorithm), 0)}

      {/* Animated plane (only in single mode) */}
      {(!hasCompare && planeXs.length > 0 && planeYs.length > 0) && (
        <motion.div
          initial={{ x: planeXs[0], y: planeYs[0] }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(3, Math.min(12, safePath.length * 0.9)),
            ease: "easeInOut",
            times,
          }}
          style={{
            position: "absolute",
            left: 0,
            top: 0,
            translate: "-50% -50%",
            zIndex: 80,
            pointerEvents: "none",
          }}
        >
          <div style={{ fontSize: 36, textShadow: "0 0 12px rgba(255,255,255,0.12)" }}>✈️</div>
        </motion.div>
      )}

      {/* Route summary / legend box (bottom-left) */}
      {(hasSinglePath || hasCompare) && (
        <div
          style={{
            position: "absolute",
            left: 18,
            bottom: 18,
            zIndex: 90,
            minWidth: 260,
            background: "rgba(7,11,20,0.78)",
            color: "#fff",
            padding: 12,
            borderRadius: 12,
            backdropFilter: "blur(6px)",
            boxShadow: "0 10px 30px rgba(2,6,23,0.6)",
            fontSize: 13,
          }}
        >
          {hasCompare ? (
            <>
              <div style={{ color: COLOR_HEX.yellow, fontWeight: 700, marginBottom: 8 }}>Algorithm Legend</div>
              {all.map((a, i) => {
                const name = a.name || `Algo ${i + 1}`;
                const colorHex = a.colorHex || algColorHex(a.name);
                return (
                  <div key={`legend-${i}`} style={{ display: "flex", gap: 8, alignItems: "flex-start", marginBottom: 8 }}>
                    <div style={{ width: 12, height: 12, borderRadius: 6, background: colorHex, boxShadow: `0 0 8px ${colorHex}66`, marginTop: 4 }} />
                    <div>
                      <div style={{ fontWeight: 700 }}>{name}</div>
                      <div style={{ color: "#cbd5e1", fontSize: 12, marginTop: 4 }}>
                        {a.data?.path?.length ? a.data.path.join(" → ") : "No path"}
                      </div>
                    </div>
                  </div>
                );
              })}
            </>
          ) : (
            <>
              <div style={{ color: COLOR_HEX.yellow, fontWeight: 700, marginBottom: 6 }}>{algorithm || "Algorithm"}</div>
              <div style={{ color: "#e6eef8", fontWeight: 700 }}>{safePath.length ? safePath.join(" → ") : "No path"}</div>
              <div style={{ color: "#9fb3cf", marginTop: 6 }}>Distance: {distance === Infinity ? "∞ (unreachable)" : distance}</div>
            </>
          )}
        </div>
      )}
    </div>
  );
}
