import React, { useEffect, useState, useRef } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

const COLOR_HEX = {
  dijkstra: "#60A5FA",
  bellman: "#34D399",
  floyd: "#F87171",
  astar: "#FACC15",
  johnson: "#C084FC",
  default: "#94A3B8",
};

export default function Visualizer({
  path = [],
  distance = 0,
  algorithm = "",
  all = null,
}) {
  const [size, setSize] = useState({ width: 1200, height: 700 });
  const canvasRef = useRef(null);

  useEffect(() => {
    const handleResize = () => {
      const mapContainer = document.getElementById("map-container");
      if (mapContainer) {
        setSize({
          width: mapContainer.offsetWidth,
          height: mapContainer.offsetHeight,
        });
      }
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // City coordinates (% of map size)
  const cityCoords = {
    A: { x: 5, y: 80 },
    B: { x: 15, y: 70 },
    C: { x: 10, y: 50 },
    D: { x: 20, y: 40 },
    E: { x: 25, y: 75 },
    F: { x: 30, y: 65 },
    G: { x: 25, y: 40 },
    H: { x: 35, y: 50 },
    I: { x: 45, y: 40 },
    J: { x: 40, y: 75 },
    K: { x: 50, y: 65 },
    L: { x: 40, y: 30 },
    M: { x: 50, y: 45 },
    N: { x: 60, y: 35 },
    O: { x: 55, y: 75 },
    P: { x: 65, y: 60 },
    Q: { x: 70, y: 45 },
  };

  const { width, height } = size;
  const scaled = {};
  for (const c in cityCoords) {
    scaled[c] = {
      x: (cityCoords[c].x / 100) * width,
      y: (cityCoords[c].y / 100) * height,
    };
  }

  const safePath = Array.isArray(path) ? path.filter((n) => scaled[n]) : [];

  // --- Draw yellow connecting route on Canvas ---
  useEffect(() => {
    if (!canvasRef.current || safePath.length < 2) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    ctx.clearRect(0, 0, width, height);

    // Route lines
    ctx.beginPath();
    ctx.lineWidth = 3;
    ctx.strokeStyle = "yellow";
    ctx.shadowBlur = 15;
    ctx.shadowColor = "yellow";

    for (let i = 0; i < safePath.length - 1; i++) {
      const start = scaled[safePath[i]];
      const end = scaled[safePath[i + 1]];
      if (start && end) {
        ctx.moveTo(start.x, start.y);
        ctx.lineTo(end.x, end.y);
      }
    }
    ctx.stroke();

    // City nodes + labels
    safePath.forEach((city) => {
      const pos = scaled[city];
      if (pos) {
        ctx.beginPath();
        ctx.arc(pos.x, pos.y, 6, 0, 2 * Math.PI);
        ctx.fillStyle = "white";
        ctx.fill();
        ctx.strokeStyle = "black";
        ctx.stroke();
        ctx.fillStyle = "yellow";
        ctx.font = "bold 14px Arial";
        ctx.fillText(city, pos.x + 8, pos.y - 8);
      }
    });
  }, [safePath, scaled, width, height]);

  // --- Return ---
  if (safePath.length < 2) {
    return (
      <div className="flex items-center justify-center h-screen text-white text-xl">
        No valid route found.
      </div>
    );
  }

  const colorHex = COLOR_HEX[algorithm.toLowerCase()] || COLOR_HEX.default;
  const planePoints = safePath.map((n) => scaled[n]);
  const planeXs = planePoints.map((p) => p.x);
  const planeYs = planePoints.map((p) => p.y);
  const times = planeXs.map((_, i) => i / (planeXs.length - 1));

  return (
    <div
      id="map-container"
      style={{
        width: "100%",
        height: "100vh",
        position: "relative",
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        overflow: "hidden",
      }}
    >
      {/* Canvas for connected yellow lines */}
      <canvas
        ref={canvasRef}
        width={width}
        height={height}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: 1,
        }}
      />

      {/* City Nodes */}
      {Object.entries(scaled).map(([node, pos]) => (
        <City key={node} node={node} pos={pos} isActive={safePath.includes(node)} />
      ))}

      {/* ✈️ Plane Animation */}
      {planeXs.length > 1 && (
        <motion.div
          initial={{ x: planeXs[0], y: planeYs[0] }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(4, safePath.length * 1.2),
            ease: "easeInOut",
            times,
            repeat: Infinity,
          }}
          style={{
            position: "absolute",
            left: 0,
            top: 0,
            translate: "-50% -50%",
            zIndex: 50,
          }}
        >
          <div style={{ fontSize: 36, textShadow: "0 0 12px rgba(255,255,255,0.2)" }}>✈️</div>
        </motion.div>
      )}

      {/* Info Box */}
      <div
        style={{
          position: "absolute",
          bottom: 20,
          left: 20,
          background: "rgba(7,11,20,0.8)",
          color: "#fff",
          padding: 12,
          borderRadius: 12,
          fontSize: 13,
          boxShadow: "0 10px 25px rgba(0,0,0,0.4)",
        }}
      >
        <div style={{ fontWeight: 700, color: colorHex, marginBottom: 8 }}>
          Algorithm: {algorithm.toUpperCase()}
        </div>
        <div>Distance: {distance === Infinity ? "∞ (unreachable)" : distance}</div>
        <div>Path: {safePath.join(" → ")}</div>
      </div>
    </div>
  );
}

// --- City component ---
function City({ node, pos, isActive }) {
  return (
    <div
      style={{
        position: "absolute",
        left: pos.x,
        top: pos.y,
        transform: "translate(-50%, -50%)",
        background: isActive ? "#facc15" : "#2563eb",
        width: 30,
        height: 30,
        borderRadius: "50%",
        color: "#fff",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        fontWeight: 700,
        fontSize: 12,
        boxShadow: "0 8px 22px rgba(37,99,235,0.25)",
        zIndex: 20,
      }}
    >
      {node}
    </div>
  );
}
