import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

export default function Visualizer({ path = [], distance = 0 }) {
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  // Responsive resizing
  useEffect(() => {
    const handleResize = () => {
      setDimensions({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const { width, height } = dimensions;

  // Coordinates for cities (relative percentages)
  const cityCoords = {
    A: { x: 12, y: 75 },
    B: { x: 35, y: 55 },
    C: { x: 28, y: 25 },
    D: { x: 55, y: 35 },
    E: { x: 75, y: 50 },
    F: { x: 88, y: 28 },
  };

  // Convert % ‚Üí actual pixels
  const scaledCoords = {};
  for (const city in cityCoords) {
    scaledCoords[city] = {
      x: (cityCoords[city].x / 100) * width,
      y: (cityCoords[city].y / 100) * height,
    };
  }

  // Generate route segments
  const segments = [];
  for (let i = 0; i < path.length - 1; i++) {
    const start = scaledCoords[path[i]];
    const end = scaledCoords[path[i + 1]];
    if (start && end) segments.push([start, end]);
  }

  return (
    <div
      className="relative w-full h-screen overflow-hidden"
      style={{
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* üîµ City markers */}
      {Object.entries(scaledCoords).map(([city, pos]) => (
        <div
          key={city}
          className="absolute w-6 h-6 bg-blue-400 rounded-full flex items-center justify-center text-sm text-white font-semibold shadow-md shadow-blue-500"
          style={{
            left: pos.x,
            top: pos.y,
            transform: "translate(-50%, -50%)",
          }}
        >
          {city}
        </div>
      ))}

      {/* üü° Glowing route lines */}
      {segments.map(([start, end], idx) => {
        const dx = end.x - start.x;
        const dy = end.y - start.y;
        const lineLength = Math.hypot(dx, dy);
        const angle = Math.atan2(dy, dx);

        return (
          <motion.div
            key={idx}
            className="absolute h-[3px] bg-yellow-400 shadow-[0_0_20px_#facc15]"
            initial={{ scaleX: 0 }}
            animate={{ scaleX: 1 }}
            transition={{ duration: 0.8, delay: idx * 0.3 }}
            style={{
              left: start.x,
              top: start.y,
              width: lineLength,
              transformOrigin: "0% 50%",
              rotate: `${angle}rad`,
            }}
          />
        );
      })}

      {/* ‚úàÔ∏è Animated airplane */}
      {segments.length > 0 && (
        <motion.div
          className="absolute text-4xl"
          initial={{
            x: segments[0][0].x,
            y: segments[0][0].y,
          }}
          animate={{
            x: segments.map((s) => s[1].x),
            y: segments.map((s) => s[1].y),
          }}
          transition={{
            duration: Math.max(6, segments.length * 2),
            ease: "easeInOut",
            times: segments.map((_, i) => i / (segments.length - 1)),
          }}
          style={{
            transform: "translate(-50%, -50%)",
            color: "red",
            textShadow: "0 0 15px red",
          }}
        >
          ‚úàÔ∏è
        </motion.div>
      )}

      {/* üó∫Ô∏è Route Summary */}
      {path && path.length > 1 && (
        <div className="absolute bottom-6 left-6 bg-slate-900/70 backdrop-blur-md p-5 rounded-xl shadow-xl border border-slate-700 text-white text-sm">
          <p className="text-lg font-semibold text-yellow-300 mb-1">
            üó∫Ô∏è Route Summary
          </p>
          <p>
            <span className="text-blue-400">Path:</span>{" "}
            {path.join(" ‚Üí ")}
          </p>
          <p>
            <span className="text-blue-400">Total Distance:</span>{" "}
            {distance} km
          </p>
        </div>
      )}
    </div>
  );
}
