import React, { useState } from "react";
import { findShortestPath } from "../algorithms";

export default function DijkstraVisualizer() {
  const [path, setPath] = useState([]);
  const [distance, setDistance] = useState(null);

  const graph = {
    A: { B: 2, C: 4 },
    B: { C: 1, D: 7 },
    C: { E: 3 },
    D: { F: 1 },
    E: { D: 2, F: 5 },
    F: {},
  };

  const handleRun = () => {
    const result = findShortestPath(graph, "A", "F");
    setPath(result.path);
    setDistance(result.distance);
  };

  return (
    <div className="flex flex-col items-center gap-4 p-6 bg-slate-800 rounded-2xl shadow-lg w-full max-w-lg">
      <h2 className="text-2xl font-semibold text-blue-400">Dijkstra’s Algorithm</h2>
      <p className="text-gray-300 text-center">
        Find the shortest path from <b>A</b> to <b>F</b>.
      </p>

      <button
        onClick={handleRun}
        className="bg-blue-500 hover:bg-blue-600 px-6 py-2 rounded-lg text-white font-semibold transition"
      >
        Run Dijkstra
      </button>

      {path.length > 0 && (
        <div className="mt-4 text-center">
          <p className="text-lg text-green-400">
            Shortest Path: {path.join(" → ")}
          </p>
          <p className="text-gray-300">Total Distance: {distance}</p>
        </div>
      )}
    </div>
  );
}
