import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

export default function Visualizer({ path = [], distance = 0, algorithm = "", all = [] }) {
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () =>
      setDimensions({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const { width, height } = dimensions;

  // city coordinates as percentages (A - Q)
  const cityCoords = {
    A: { x: 5, y: 80 },
    B: { x: 15, y: 70 },
    C: { x: 10, y: 50 },
    D: { x: 20, y: 40 },
    E: { x: 25, y: 75 },
    F: { x: 30, y: 65 },
    G: { x: 25, y: 40 },
    H: { x: 35, y: 50 },
    I: { x: 45, y: 40 },
    J: { x: 40, y: 75 },
    K: { x: 50, y: 65 },
    L: { x: 40, y: 30 },
    M: { x: 50, y: 45 },
    N: { x: 60, y: 35 },
    O: { x: 55, y: 75 },
    P: { x: 65, y: 60 },
    Q: { x: 70, y: 45 },
  };

  // scaled pixel coordinates
  const scaledCoords = {};
  for (const city in cityCoords) {
    scaledCoords[city] = {
      x: (cityCoords[city].x / 100) * width,
      y: (cityCoords[city].y / 100) * height,
    };
  }

  // helper: draw a path (returns array of motion divs)
  const drawPath = (p = [], color = "yellow-400", delay = 0) => {
    const segments = [];
    for (let i = 0; i < p.length - 1; i++) {
      const s = scaledCoords[p[i]];
      const e = scaledCoords[p[i + 1]];
      if (!s || !e) continue;
      const dx = e.x - s.x;
      const dy = e.y - s.y;
      const len = Math.hypot(dx, dy);
      const angle = Math.atan2(dy, dx);
      segments.push(
        <motion.div
          key={`${color}-${p[i]}-${p[i + 1]}-${i}`}
          className={`absolute h-[3px] bg-${color} shadow-[0_0_15px]`}
          initial={{ scaleX: 0 }}
          animate={{ scaleX: 1 }}
          transition={{ duration: 0.9, delay: delay + i * 0.2 }}
          style={{
            left: s.x,
            top: s.y,
            width: len,
            transformOrigin: "0% 50%",
            rotate: `${angle}rad`,
          }}
        />
      );
    }
    return segments;
  };

  // build standard segments for single path (used for plane animation)
  const singleSegments = [];
  if (Array.isArray(path) && path.length > 1) {
    for (let i = 0; i < path.length - 1; i++) {
      const s = scaledCoords[path[i]];
      const e = scaledCoords[path[i + 1]];
      if (s && e) singleSegments.push([s, e]);
    }
  }

  // plane animation helpers (for single algorithm mode)
  const planeInitial = singleSegments.length > 0 ? singleSegments[0][0] : null;
  const planeXs = path && path.length ? path.map((city) => scaledCoords[city]?.x || 0) : [];
  const planeYs = path && path.length ? path.map((city) => scaledCoords[city]?.y || 0) : [];

  return (
    <div
      className="relative w-full h-screen overflow-hidden"
      style={{
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* City markers */}
      {Object.entries(scaledCoords).map(([city, pos]) => (
        <div
          key={city}
          className="absolute w-6 h-6 bg-blue-400 rounded-full flex items-center justify-center text-sm text-white font-semibold shadow-md shadow-blue-500"
          style={{
            left: pos.x,
            top: pos.y,
            transform: "translate(-50%, -50%)",
          }}
        >
          {city}
        </div>
      ))}

      {/* If compare mode, draw all algorithm paths with different colors */}
      {algorithm === "compare" && Array.isArray(all) && all.length
        ? all.flatMap((algo, idx) => {
            const color = algo.color || "yellow-400";
            const p = algo.data?.path || [];
            return drawPath(p, color, idx * 0.3);
          })
        : // single path mode: draw main path in yellow
          drawPath(path, "yellow-400")}

      {/* Animated airplane for single-path mode */}
      {algorithm !== "compare" && planeInitial && path && path.length > 0 && (
        <motion.div
          className="absolute text-4xl"
          initial={{ x: planeInitial.x, y: planeInitial.y }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(6, path.length * 1.2),
            ease: "easeInOut",
            times: path.map((_, i) => (path.length === 1 ? 0 : i / (path.length - 1))),
          }}
          style={{
            transform: "translate(-50%, -50%)",
            color: "red",
            textShadow: "0 0 15px red",
          }}
        >
          ‚úàÔ∏è
        </motion.div>
      )}

      {/* Route summary box (bottom-left) for single path */}
      {algorithm !== "compare" && path && path.length > 0 && (
        <div className="absolute bottom-6 left-6 bg-slate-900/70 backdrop-blur-md p-5 rounded-xl shadow-xl border border-slate-700 text-white text-sm">
          <p className="text-lg font-semibold text-yellow-300 mb-1">üó∫Ô∏è Route Summary</p>
          <p>
            <span className="text-blue-400">Algorithm:</span>{" "}
            {typeof algorithm === "string" ? algorithm.toUpperCase() : algorithm}
          </p>
          <p>
            <span className="text-blue-400">Path:</span> {path.join(" ‚Üí ")}
          </p>
          <p>
            <span className="text-blue-400">Total Distance:</span> {distance}
          </p>
        </div>
      )}

      {/* Legend for compare mode */}
      {algorithm === "compare" && Array.isArray(all) && all.length > 0 && (
        <div className="absolute bottom-6 left-6 bg-slate-900/70 p-4 rounded-xl shadow-xl border border-slate-700 text-sm">
          <h3 className="font-semibold mb-2 text-yellow-300">Algorithm Legend</h3>
          {all.map((a, i) => (
            <p key={i} className="flex items-center gap-2">
              <span
                className="inline-block w-3 h-3 rounded-full"
                style={{
                  background:
                    a.color === "blue-400"
                      ? "rgb(96,165,250)"
                      : a.color === "green-400"
                      ? "rgb(74,222,128)"
                      : a.color === "red-400"
                      ? "rgb(248,113,113)"
                      : a.color === "yellow-400"
                      ? "rgb(250,204,21)"
                      : a.color === "purple-400"
                      ? "rgb(192,132,252)"
                      : "yellow",
                  boxShadow: "0 0 8px rgba(0,0,0,0.2)",
                }}
              />
              <span>{a.name} ‚Äî {a.data?.path?.length ? a.data.path.join(" ‚Üí ") : "No path"}</span>
            </p>
          ))}
        </div>
      )}
    </div>
  );
}
