import React, { useMemo } from "react";
import { motion } from "framer-motion";

// Color set for consistent look
const algoColors = {
  dijkstra: "#60A5FA",
  bellman: "#34D399",
  "floyd–warshall": "#F87171",
  floyd: "#F87171",
  "a*": "#FACC15",
  johnson: "#C084FC",
};

export default function Visualizer({ path, distance, algorithm, all }) {
  // Same 15-node coordinate layout
  const coords = {
    A: { x: 5, y: 80 },
    B: { x: 15, y: 70 },
    C: { x: 10, y: 50 },
    D: { x: 20, y: 40 },
    E: { x: 25, y: 75 },
    F: { x: 30, y: 65 },
    G: { x: 25, y: 40 },
    H: { x: 35, y: 50 },
    I: { x: 45, y: 40 },
    J: { x: 40, y: 75 },
    K: { x: 50, y: 65 },
    L: { x: 40, y: 30 },
    M: { x: 50, y: 45 },
    N: { x: 60, y: 35 },
    O: { x: 55, y: 75 },
    P: { x: 65, y: 60 },
    Q: { x: 70, y: 45 },
  };

  // scale coords to view size
  const scaled = useMemo(() => {
    const w = window.innerWidth;
    const h = window.innerHeight;
    const scaleX = w / 100;
    const scaleY = h / 100;
    const obj = {};
    for (const [key, val] of Object.entries(coords)) {
      obj[key] = { x: val.x * scaleX, y: val.y * scaleY };
    }
    return obj;
  }, []);

  // helper to draw line between two nodes
  const Segment = ({ from, to, color }) => {
    const a = scaled[from];
    const b = scaled[to];
    if (!a || !b) return null;

    const dx = b.x - a.x;
    const dy = b.y - a.y;
    const len = Math.sqrt(dx * dx + dy * dy);
    const angle = Math.atan2(dy, dx);

    return (
      <motion.div
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ duration: 0.6 }}
        style={{
          position: "absolute",
          left: a.x,
          top: a.y,
          width: len,
          height: 4,
          transformOrigin: "0% 50%",
          transform: `rotate(${angle}rad)`,
          background: color,
          borderRadius: 6,
          boxShadow: `0 0 8px ${color}aa`,
          pointerEvents: "none",
        }}
      />
    );
  };

  // --- Single Algorithm View ---
  if (algorithm !== "compare") {
    if (!Array.isArray(path) || path.length < 2)
      return (
        <div className="flex items-center justify-center h-screen text-xl text-white">
          No valid route found.
        </div>
      );

    const color = algoColors[algorithm] || "#60A5FA";

    return (
      <div className="relative w-full h-full bg-slate-900">
        {path.map((node, i) =>
          i < path.length - 1 ? (
            <Segment key={i} from={node} to={path[i + 1]} color={color} />
          ) : null
        )}

        {path.map((node) => {
          const c = scaled[node];
          if (!c) return null;
          return (
            <motion.div
              key={node}
              className="absolute flex items-center justify-center rounded-full text-xs font-semibold text-black bg-yellow-300"
              style={{
                left: c.x - 8,
                top: c.y - 8,
                width: 20,
                height: 20,
              }}
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.2 }}
            >
              {node}
            </motion.div>
          );
        })}

        <div className="absolute bottom-6 left-1/2 -translate-x-1/2 bg-slate-800/80 px-6 py-3 rounded-xl text-center text-white shadow-lg border border-slate-700">
          <div className="text-lg font-semibold">
            Algorithm:{" "}
            <span className="text-blue-400 capitalize">{algorithm}</span>
          </div>
          <div>
            Total Distance:{" "}
            <span className="font-bold text-green-400">{distance}</span>
          </div>
          <div>Path: {path.join(" → ")}</div>
        </div>
      </div>
    );
  }

  // --- Compare All Algorithms Mode ---
  return (
    <div className="relative w-full h-full bg-slate-900">
      {all?.map((a, i) => {
        const clr = a.colorHex || algoColors[a.name.toLowerCase()] || "#888";
        if (!a.data?.path || a.data.path.length < 2) return null;

        return (
          <React.Fragment key={i}>
            {a.data.path.map((node, j) =>
              j < a.data.path.length - 1 ? (
                <Segment
                  key={j}
                  from={node}
                  to={a.data.path[j + 1]}
                  color={clr}
                />
              ) : null
            )}
          </React.Fragment>
        );
      })}

      {/* City markers */}
      {Object.entries(scaled).map(([n, c]) => (
        <div
          key={n}
          className="absolute text-xs text-white"
          style={{
            left: c.x - 5,
            top: c.y - 5,
          }}
        >
          {n}
        </div>
      ))}

      {/* --- Clean summary box --- */}
      <div className="absolute bottom-6 left-1/2 -translate-x-1/2 bg-slate-800/90 p-6 rounded-xl shadow-xl border border-slate-700 max-w-xl w-[90%]">
        <h2 className="text-xl font-bold mb-2 text-blue-400 text-center">
          ✈️ Algorithm Comparison Summary
        </h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 gap-2 text-sm">
          {all?.map((a, i) => (
            <div
              key={i}
              className="bg-slate-700/50 p-2 rounded-lg text-center border border-slate-600"
              style={{ borderTop: `3px solid ${a.colorHex}` }}
            >
              <div className="font-semibold text-white mb-1">{a.name}</div>
              <div className="text-green-400">
                Dist: {a.data?.distance ?? "–"}
              </div>
              <div className="text-xs text-gray-300">
                {a.data?.path?.join(" → ") || "No Path"}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
