import React ,{ useEffect, useState } from "react";;
import { motion } from "framer-motion";
import map from "./assets/map.png";

// Algorithm color palette
const COLOR_HEX = {
  dijkstra: "#60A5FA",
  bellman: "#34D399",
  floyd: "#F87171",
  astar: "#FACC15",
  johnson: "#C084FC",
  default: "#94A3B8",
};

export default function Visualizer({ path = [], distance, algorithm, all }) {
  const coords = {
    A: { x: 5, y: 80 },
    B: { x: 15, y: 70 },
    C: { x: 10, y: 50 },
    D: { x: 20, y: 40 },
    E: { x: 25, y: 75 },
    F: { x: 30, y: 65 },
    G: { x: 25, y: 40 },
    H: { x: 35, y: 50 },
    I: { x: 45, y: 40 },
    J: { x: 40, y: 75 },
    K: { x: 50, y: 65 },
    L: { x: 40, y: 30 },
    M: { x: 50, y: 45 },
    N: { x: 60, y: 35 },
    O: { x: 55, y: 75 },
    P: { x: 65, y: 60 },
    Q: { x: 70, y: 45 },
  };

  const scaleX = 15; // Adjust these for spacing
  const scaleY = 10;

  // Function to get screen coordinates
  const getPos = (city) => ({
    x: coords[city].x * scaleX,
    y: 800 - coords[city].y * scaleY, // invert Y for natural orientation
  });

  const lines = [];
  for (let i = 0; i < path.length - 1; i++) {
    const from = getPos(path[i]);
    const to = getPos(path[i + 1]);
    lines.push({ from, to });
  }

  return (
    <div className="w-full h-full relative bg-slate-900 overflow-hidden">
      <svg
        viewBox="0 0 1200 800"
        className="absolute w-full h-full"
        xmlns="http://www.w3.org/2000/svg"
      >
        {/* Draw connection lines */}
        {lines.map((line, i) => (
          <motion.line
            key={i}
            x1={line.from.x}
            y1={line.from.y}
            x2={line.to.x}
            y2={line.to.y}
            stroke="#60A5FA"
            strokeWidth="4"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ duration: 0.8, delay: i * 0.3 }}
          />
        ))}

        {/* Draw nodes */}
        {Object.entries(coords).map(([city, { x, y }]) => (
          <g key={city}>
            <circle
              cx={x * scaleX}
              cy={800 - y * scaleY}
              r={path.includes(city) ? 14 : 10}
              fill={path.includes(city) ? "#FACC15" : "#38BDF8"}
              stroke="#fff"
              strokeWidth="2"
            />
            <text
              x={x * scaleX}
              y={800 - y * scaleY - 18}
              textAnchor="middle"
              fill="white"
              fontSize="18"
              fontWeight="bold"
            >
              {city}
            </text>
          </g>
        ))}
      </svg>

      {/* Path details */}
      {path.length > 1 && (
        <div className="absolute bottom-6 left-6 bg-slate-800/80 px-6 py-3 rounded-xl text-white text-lg shadow-lg">
          <p>
            <span className="font-semibold text-blue-400 uppercase">
              {algorithm}
            </span>{" "}
            Path: {path.join(" → ")}
          </p>
          <p className="text-sm text-slate-300 mt-1">
            Total Distance: {distance}
          </p>
        </div>
      )}
    </div>
  );
}
