import React, { useEffect, useState, useMemo } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

const COLOR_HEX = {
  dijkstra: "#60A5FA",
  bellman: "#34D399",
  floyd: "#F87171",
  astar: "#FACC15",
  johnson: "#C084FC",
  default: "#94A3B8",
};

export default function Visualizer({ path = [], distance = 0, algorithm = "", all = null }) {
  const [size, setSize] = useState({ width: 1200, height: 700 });
  const canvasRef = React.useRef(null);

  useEffect(() => {
    const handleResize = () => {
      const mapContainer = document.getElementById("map-container");
      if (mapContainer) {
        setSize({
          width: mapContainer.offsetWidth,
          height: mapContainer.offsetHeight,
        });
      }
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Same coordinates from App.js (consider passing as prop to avoid duplication)
  const cityCoords = {
    A: { x: 5, y: 80 },
    B: { x: 15, y: 70 },
    C: { x: 10, y: 50 },
    D: { x: 20, y: 40 },
    E: { x: 25, y: 75 },
    F: { x: 30, y: 65 },
    G: { x: 25, y: 40 },
    H: { x: 35, y: 50 },
    I: { x: 45, y: 40 },
    J: { x: 40, y: 75 },
    K: { x: 50, y: 65 },
    L: { x: 40, y: 30 },
    M: { x: 50, y: 45 },
    N: { x: 60, y: 35 },
    O: { x: 55, y: 75 },
    P: { x: 65, y: 60 },
    Q: { x: 70, y: 45 },
  };

  // Memoize scaled coordinates for performance
  const scaled = useMemo(() => {
    const { width, height } = size;
    const result = {};
    for (const c in cityCoords) {
      result[c] = {
        x: (cityCoords[c].x / 100) * width,
        y: (cityCoords[c].y / 100) * height,
      };
    }
    return result;
  }, [size]);

  // Validate and filter path
  const safePath = useMemo(() => {
  if (algorithm === "compare" && Array.isArray(all) && all.length > 0) {
    // Use the first algorithm's path to display plane
    return all[0].data.path || [];
  }
  if (!Array.isArray(path)) return [];
  return path.filter((n) => scaled[n]);
  }, [path, scaled, algorithm, all]);


  // Determine active cities for highlighting
  const activeCities = useMemo(() => {
    if (algorithm === "compare" && Array.isArray(all)) {
      const active = new Set();
      all.forEach((alg) => {
        if (alg.data?.path && Array.isArray(alg.data.path)) {
          alg.data.path.forEach((node) => active.add(node));
        }
      });
      return active;
    }
    return new Set(safePath);
  }, [algorithm, all, safePath]);

  // --- CANVAS DRAWING ---
  useEffect(() => {
    if (!canvasRef.current) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    ctx.clearRect(0, 0, size.width, size.height);

    if (algorithm === "compare" && Array.isArray(all)) {
      // Comparison mode: Draw colored lines for each algorithm
      all.forEach((alg) => {
        if (!alg.data?.path || !Array.isArray(alg.data.path) || alg.data.path.length < 2) return;
        ctx.beginPath();
        ctx.lineWidth = 3;
        ctx.strokeStyle = alg.colorHex || COLOR_HEX.default;

        for (let i = 0; i < alg.data.path.length - 1; i++) {
          const start = scaled[alg.data.path[i]];
          const end = scaled[alg.data.path[i + 1]];
          if (start && end) {
            ctx.moveTo(start.x, start.y);
            ctx.lineTo(end.x, end.y);
          }
        }
        ctx.stroke();
      });
    } else if (Array.isArray(path) && path.length >= 2) {
      // Single mode: Draw yellow lines
      ctx.beginPath();
      ctx.lineWidth = 3;
      ctx.strokeStyle = COLOR_HEX[algorithm.toLowerCase()] || "yellow";

      for (let i = 0; i < path.length - 1; i++) {
        const start = scaled[path[i]];
        const end = scaled[path[i + 1]];
        if (start && end) {
          ctx.moveTo(start.x, start.y);
          ctx.lineTo(end.x, end.y);
        }
      }
      ctx.stroke();
    }
  }, [path, scaled, size, algorithm, all]);

  // --- MULTI-ALGORITHM COMPARISON MODE ---
  if (algorithm === "compare" && Array.isArray(all)) {
    return (
      <div
        id="map-container"
        style={{
          width: "100%",
          height: "100vh",
          position: "relative",
          backgroundImage: `url(${map})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          overflow: "hidden",
        }}
      >
        <canvas
          ref={canvasRef}
          width={size.width}
          height={size.height}
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            zIndex: 1,
          }}
        />

        {Object.entries(scaled).map(([node, pos]) => (
          <City key={node} node={node} pos={pos} isActive={activeCities.has(node)} />
        ))}

        <div
          style={{
            position: "absolute",
            bottom: 20,
            left: 20,
            background: "rgba(15,20,30,0.85)",
            padding: "12px 16px",
            borderRadius: 10,
            color: "white",
            fontSize: 14,
            backdropFilter: "blur(5px)",
          }}
        >
          <b>Algorithm Comparison</b>
          <ul style={{ marginTop: 8 }}>
            {all.map((a) => (
              <li key={a.name} style={{ color: a.colorHex || COLOR_HEX.default }}>
                {a.name}: {a.data?.distance ?? "∞"}
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }

  // --- SINGLE ALGORITHM MODE ---
  if (safePath.length < 2) {
    return (
      <div className="flex items-center justify-center h-screen text-white text-xl">
        No valid route found.
      </div>
    );
  }

  const planePoints = safePath.map((n) => scaled[n]).filter(Boolean);
  const planeXs = planePoints.map((p) => p.x);
  const planeYs = planePoints.map((p) => p.y);
  const times = planeXs.length > 1 ? planeXs.map((_, i) => i / (planeXs.length - 1)) : [];

  return (
    <div
      id="map-container"
      style={{
        width: "100%",
        height: "100vh",
        position: "relative",
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        overflow: "hidden",
      }}
    >
      <canvas
        ref={canvasRef}
        width={size.width}
        height={size.height}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: 1,
        }}
      />

      {Object.entries(scaled).map(([node, pos]) => (
        <City key={node} node={node} pos={pos} isActive={activeCities.has(node)} />
      ))}

      {/* ✈️ Plane Animation */}
      {planeXs.length > 1 && planeYs.length === planeXs.length && (
        <motion.div
          initial={{ x: planeXs[0], y: planeYs[0] }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(4, safePath.length * 1.2),
            ease: "easeInOut",
            times,
            repeat: 0,
          }}
          style={{
            position: "absolute",
            left: 0,
            top: 0,
            transform: "translate(-50%, -50%)",
            zIndex: 50,
          }}
        >
          <div style={{ fontSize: 36, textShadow: "0 0 12px rgba(255,255,255,0.2)" }}>✈️</div>
        </motion.div>
      )}

      {/* Info */}
      <div
        style={{
          position: "absolute",
          bottom: 20,
          left: 20,
          background: "rgba(7,11,20,0.8)",
          color: "#fff",
          padding: 12,
          borderRadius: 12,
          fontSize: 13,
          boxShadow: "0 10px 25px rgba(0,0,0,0.4)",
        }}
      >
        <div style={{ fontWeight: 700, color: COLOR_HEX[algorithm.toLowerCase()] || COLOR_HEX.default, marginBottom: 8 }}>
          Algorithm: {algorithm ? algorithm.toUpperCase() : "UNKNOWN"}
        </div>
        <div>Distance: {distance === Infinity ? "∞ (unreachable)" : distance}</div>
        <div>Path: {safePath.join(" → ")}</div>
      </div>
    </div>
  );
}

// --- Small components ---
function City({ node, pos, isActive }) {
  return (
    <div
      style={{
        position: "absolute",
        left: pos.x,
        top: pos.y,
        transform: "translate(-50%, -50%)",
        background: isActive ? "#facc15" : "#2563eb",
        width: 30,
        height: 30,
        borderRadius: "50%",
        color: "#fff",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        fontWeight: 700,
        fontSize: 12,
        boxShadow: "0 8px 22px rgba(37,99,235,0.25)",
        zIndex: 20,
      }}
    >
      {node}
    </div>
  );
}
