import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

// Color mapping for algorithms
const COLOR_HEX = {
  blue: "#60A5FA",
  green: "#34D399",
  red: "#F87171",
  yellow: "#FACC15",
  purple: "#C084FC",
  gray: "#94A3B8",
};

export default function Visualizer({ path = [], distance = 0, algorithm = "", all = [] }) {
  const [size, setSize] = useState({ width: 1200, height: 700 });

  // Adjust to window size
  useEffect(() => {
    const onResize = () => setSize({ width: window.innerWidth, height: window.innerHeight });
    onResize();
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  const { width, height } = size;
  // Fixed coordinates for all cities (in % of map area)
  const cityCoords = {
    A: { x: 5, y: 80 },
    B: { x: 15, y: 70 },
    C: { x: 10, y: 50 },
    D: { x: 20, y: 40 },
    E: { x: 25, y: 75 },
    F: { x: 30, y: 65 },
    G: { x: 25, y: 40 },
    H: { x: 35, y: 50 },
    I: { x: 45, y: 40 },
    J: { x: 40, y: 75 },
    K: { x: 50, y: 65 },
    L: { x: 40, y: 30 },
    M: { x: 50, y: 45 },
    N: { x: 60, y: 35 },
    O: { x: 55, y: 75 },
    P: { x: 65, y: 60 },
    Q: { x: 70, y: 45 },
  };

  // Convert percentages to pixel values based on current screen size
  const scaled = {};
  for (const k in cityCoords) {
    scaled[k] = {
      x: (cityCoords[k].x / 100) * width,
      y: (cityCoords[k].y / 100) * height,
    };
  }
  // Draw a single route line between two cities
  function Segment({ from, to, colorHex, delay = 0 }) {
    const a = scaled[from];
    const b = scaled[to];
    if (!a || !b) return null;

    const dx = b.x - a.x;
    const dy = b.y - a.y;
    const len = Math.hypot(dx, dy);
    const angle = Math.atan2(dy, dx);

    return (
      <motion.div
        key={`seg-${from}-${to}`}
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ duration: 0.9, delay }}
        style={{
          position: "absolute",
          left: a.x,
          top: a.y,
          width: Math.max(1, len),
          height: 3,
          background: colorHex,
          transformOrigin: "0% 50%",
          transform: `rotate(${angle}rad)`,
          boxShadow: `0 0 10px ${colorHex}66`,
          zIndex: 10,
        }}
      />
    );
  }
  // --- Ensure only valid cities in path ---
  const safePath = Array.isArray(path) ? path.filter((n) => n && scaled[n]) : [];

  // --- Prepare segments for the plane animation ---
  const planeSegments = [];
  for (let i = 0; i < safePath.length - 1; i++) {
    const a = scaled[safePath[i]];
    const b = scaled[safePath[i + 1]];
    if (a && b) planeSegments.push([a, b]);
  }

  // --- Extract X and Y coordinates for plane animation ---
  const planeXs = planeSegments.map((s) => s[0].x).concat(
    planeSegments.length ? planeSegments[planeSegments.length - 1][1].x : []
  );
  const planeYs = planeSegments.map((s) => s[0].y).concat(
    planeSegments.length ? planeSegments[planeSegments.length - 1][1].y : []
  );

  // --- Define animation timing for plane ---
  const times = planeXs.length > 1
    ? planeXs.map((_, i) => i / (planeXs.length - 1))
    : [0];
  // --- Helper: Pick color based on algorithm ---
  function algColorHex(name) {
    if (!name) return "#FACC15"; // yellow default
    const key = String(name).toLowerCase();
    if (key.includes("dijkstra")) return "#60A5FA"; // blue
    if (key.includes("bellman")) return "#34D399"; // green
    if (key.includes("floyd")) return "#F87171"; // red
    if (key.includes("a*") || key.includes("astar")) return "#FACC15"; // yellow
    if (key.includes("johnson")) return "#C084FC"; // purple
    return "#94A3B8"; // gray fallback
  }

  // --- If invalid path ---
  if (!safePath.length || safePath.length < 2) {
    return (
      <div className="flex items-center justify-center h-screen text-xl text-white">
        No valid route found.
      </div>
    );
  }

  // --- Color for current algorithm ---
  const colorHex = algColorHex(algorithm);

  // --- MAIN RETURN (map + route + plane + city markers + summary) ---
  return (
    <div
      style={{
        width: "100%",
        height: "100vh",
        position: "relative",
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* Draw route segments */}
      {safePath.map(
        (node, i) =>
          i < safePath.length - 1 && (
            <Segment
              key={`seg-${i}`}
              from={node}
              to={safePath[i + 1]}
              colorHex={colorHex}
              delay={i * 0.15}
            />
          )
      )}

      {/* City markers */}
      {Object.entries(scaled).map(([node, pos]) => (
        <div
          key={node}
          style={{
            position: "absolute",
            left: pos.x,
            top: pos.y,
            transform: "translate(-50%, -50%)",
            background: "#2563eb",
            width: 30,
            height: 30,
            borderRadius: "50%",
            color: "#fff",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontWeight: 700,
            fontSize: 12,
            boxShadow: "0 8px 22px rgba(37,99,235,0.18)",
            zIndex: 20,
          }}
        >
          {node}
        </div>
      ))}

      {/* Plane animation */}
      {planeXs.length > 1 && (
        <motion.div
          initial={{ x: planeXs[0], y: planeYs[0] }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(3, Math.min(12, safePath.length * 0.9)),
            ease: "easeInOut",
            times,
          }}
          style={{
            position: "absolute",
            left: 0,
            top: 0,
            translate: "-50% -50%",
            zIndex: 50,
          }}
        >
          <div style={{ fontSize: 36, textShadow: "0 0 12px rgba(255,255,255,0.12)" }}>
            ✈️
          </div>
        </motion.div>
      )}

      {/* Summary info */}
      <div
        style={{
          position: "absolute",
          bottom: 18,
          left: 18,
          background: "rgba(7,11,20,0.78)",
          color: "#fff",
          padding: 12,
          borderRadius: 12,
          backdropFilter: "blur(6px)",
          boxShadow: "0 10px 30px rgba(2,6,23,0.6)",
          fontSize: 13,
          minWidth: 260,
        }}
      >
        <div style={{ fontWeight: 700, color: "#FACC15", marginBottom: 8 }}>
          Algorithm: {algorithm}
        </div>
        <div>Distance: {distance === Infinity ? "∞ (unreachable)" : distance}</div>
        <div>Path: {safePath.join(" → ")}</div>
      </div>
    </div>
  );
}
