import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

export default function Visualizer({ path = [], distance = 0, algorithm = "", all = [] }) {
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () =>
      setDimensions({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const { width, height } = dimensions;

  // --- NEW: coordinates tuned to lie on land on a world map image ---
  // percentages (x: 0..100 left->right, y: 0..100 top->bottom)
  // Three India points: K, L, M
  const cityCoords = {
    A: { x: 12, y: 45 }, // Western USA (California)
    B: { x: 20, y: 35 }, // Mexico / Gulf
    C: { x: 26, y: 48 }, // Caribbean / Central America
    D: { x: 34, y: 30 }, // Eastern USA
    E: { x: 28, y: 70 }, // Brazil (S. America)
    F: { x: 40, y: 78 }, // Southern Africa
    G: { x: 45, y: 40 }, // UK / Western Europe
    H: { x: 50, y: 50 }, // Mid-Atlantic near Africa (bridge node)
    I: { x: 58, y: 35 }, // Middle East
    J: { x: 62, y: 30 }, // Pakistan / West India border
    // India cluster (three points)
    K: { x: 68, y: 40 }, // North-West India (e.g., Rajasthan)
    L: { x: 71, y: 48 }, // Central India (e.g., Maharashtra)
    M: { x: 73, y: 57 }, // South India (e.g., Tamil Nadu/Kerala)
    N: { x: 76, y: 33 }, // Nepal / Bangladesh area / NE of India
    O: { x: 80, y: 60 }, // Southeast Asia / Thailand
    P: { x: 85, y: 50 }, // China/SEA region
    Q: { x: 90, y: 45 }, // East Asia (Japan region)
  };

  // Convert percents to pixels
  const scaledCoords = {};
  for (const city in cityCoords) {
    scaledCoords[city] = {
      x: (cityCoords[city].x / 100) * width,
      y: (cityCoords[city].y / 100) * height,
    };
  }

  // draw a path as a series of rotated divs
  const drawPath = (p = [], color = "yellow-400", delay = 0) => {
    const segments = [];
    for (let i = 0; i < (p?.length || 0) - 1; i++) {
      const a = scaledCoords[p[i]];
      const b = scaledCoords[p[i + 1]];
      if (!a || !b) continue;
      const dx = b.x - a.x;
      const dy = b.y - a.y;
      const len = Math.hypot(dx, dy);
      const angle = Math.atan2(dy, dx);
      segments.push(
        <motion.div
          key={`${color}-${p[i]}-${p[i + 1]}-${i}`}
          className={`absolute h-[3px] bg-${color} shadow-[0_0_12px_rgba(0,0,0,0.35)]`}
          initial={{ scaleX: 0 }}
          animate={{ scaleX: 1 }}
          transition={{ duration: 0.9, delay: delay + i * 0.18 }}
          style={{
            left: a.x,
            top: a.y,
            width: len,
            transformOrigin: "0% 50%",
            rotate: `${angle}rad`,
            zIndex: 5,
          }}
        />
      );
    }
    return segments;
  };

  // Prepare single-path segments (for plane animation)
  const singleSegments = [];
  if (Array.isArray(path) && path.length > 1) {
    for (let i = 0; i < path.length - 1; i++) {
      const s = scaledCoords[path[i]];
      const e = scaledCoords[path[i + 1]];
      if (s && e) singleSegments.push([s, e]);
    }
  }

  const planeInitial = singleSegments.length > 0 ? singleSegments[0][0] : null;
  const planeXs = path && path.length ? path.map((c) => scaledCoords[c]?.x || 0) : [];
  const planeYs = path && path.length ? path.map((c) => scaledCoords[c]?.y || 0) : [];

  return (
    <div
      className="relative w-full h-screen overflow-hidden"
      style={{
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* --- City markers (on top) --- */}
      {Object.entries(scaledCoords).map(([city, pos]) => (
        <div
          key={city}
          className="absolute w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-xs text-white font-semibold shadow-lg"
          style={{
            left: pos.x,
            top: pos.y,
            transform: "translate(-50%, -50%)",
            zIndex: 40,
          }}
        >
          {city}
        </div>
      ))}

      {/* --- Draw routes --- */}
      {algorithm === "compare" && Array.isArray(all) && all.length
        ? all.flatMap((algo, idx) => {
            const color = algo.color || "yellow-400";
            const p = algo.data?.path || [];
            return drawPath(p, color, idx * 0.25);
          })
        : drawPath(path, "yellow-400")}

      {/* --- Animated plane for single algorithm mode --- */}
      {algorithm !== "compare" && planeInitial && path && path.length > 0 && (
        <motion.div
          className="absolute text-4xl"
          initial={{ x: planeInitial.x, y: planeInitial.y }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(6, path.length * 1.2),
            ease: "easeInOut",
            times: path.map((_, i) => (path.length === 1 ? 0 : i / (path.length - 1))),
            repeat: 0,
          }}
          style={{
            transform: "translate(-50%, -50%)",
            color: "#ff6b6b",
            textShadow: "0 0 12px rgba(255,107,107,0.8)",
            zIndex: 50,
          }}
        >
          ‚úàÔ∏è
        </motion.div>
      )}

      {/* --- Route summary (top-center, compact) --- */}
      {algorithm !== "compare" && path && path.length > 0 && (
        <div
          className="absolute top-4 left-1/2 -translate-x-1/2 bg-slate-900/70 backdrop-blur-md p-3 rounded-xl shadow-xl border border-slate-700 text-white text-sm"
          style={{ zIndex: 60, minWidth: 260 }}
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-xs text-yellow-300 font-semibold">üó∫Ô∏è Route Summary</p>
              <p className="text-xs"><span className="text-blue-300">Algorithm:</span> {typeof algorithm === "string" ? algorithm.toUpperCase() : algorithm}</p>
              <p className="text-xs"><span className="text-blue-300">Path:</span> {path.join(" ‚Üí ")}</p>
              <p className="text-xs"><span className="text-blue-300">Distance:</span> {distance}</p>
            </div>
          </div>
        </div>
      )}

      {/* --- Legend for compare mode --- */}
      {algorithm === "compare" && Array.isArray(all) && all.length > 0 && (
        <div className="absolute bottom-6 left-6 bg-slate-900/70 p-4 rounded-xl shadow-xl border border-slate-700 text-sm z-60">
          <h3 className="font-semibold mb-2 text-yellow-300">Algorithm Legend</h3>
          {all.map((a, i) => (
            <p key={i} className="flex items-center gap-2">
              <span
                className="inline-block w-3 h-3 rounded-full"
                style={{
                  background:
                    a.color === "blue-400"
                      ? "rgb(96,165,250)"
                      : a.color === "green-400"
                      ? "rgb(74,222,128)"
                      : a.color === "red-400"
                      ? "rgb(248,113,113)"
                      : a.color === "yellow-400"
                      ? "rgb(250,204,21)"
                      : a.color === "purple-400"
                      ? "rgb(192,132,252)"
                      : "yellow",
                  boxShadow: "0 0 8px rgba(0,0,0,0.2)",
                }}
              />
              <span className="text-xs">{a.name} ‚Äî {a.data?.path?.length ? a.data.path.join(" ‚Üí ") : "No path"}</span>
            </p>
          ))}
        </div>
      )}
    </div>
  );
}
