import React, { useState } from "react";
import AlgorithmSelector from "./components/AlgorithmSelector";
import Visualizer from "./components/Visualizer";

function App() {
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("");

  return (
    <div className="min-h-screen flex flex-col items-center justify-start gap-6 bg-gradient-to-b from-slate-900 to-blue-950 text-white p-6">
      <h1 className="text-4xl font-bold text-blue-400 mt-4">
        ⚙️ DSA Algorithm Visualizer
      </h1>

      {/* Algorithm Selection Panel */}
      <AlgorithmSelector
        selected={selectedAlgorithm}
        setSelected={setSelectedAlgorithm}
      />

      {/* Visualizer Area */}
      <Visualizer algorithm={selectedAlgorithm} />
    </div>
  );
}

export default App;
