import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import Visualizer from "./Visualizer";
import StatsCard from "./StatsCard";
import { findShortestPath as dijkstra } from "./dijkstra";

// --------------------------------------------
//  Additional algorithms for comparison
// --------------------------------------------

// Bellman-Ford
function bellmanFord(graph, start, end) {
  const vertices = Object.keys(graph);
  const dist = {};
  const prev = {};
  vertices.forEach((v) => {
    dist[v] = Infinity;
    prev[v] = null;
  });
  dist[start] = 0;

  for (let i = 0; i < vertices.length - 1; i++) {
    for (let u of vertices) {
      for (let v in graph[u]) {
        const weight = graph[u][v];
        if (dist[u] + weight < dist[v]) {
          dist[v] = dist[u] + weight;
          prev[v] = u;
        }
      }
    }
  }

  const path = [];
  let curr = end;
  while (curr) {
    path.unshift(curr);
    curr = prev[curr];
  }

  return { path, distance: dist[end] };
}

// Floyd–Warshall
function floydWarshall(graph, start, end) {
  const nodes = Object.keys(graph);
  const dist = {};
  const next = {};

  for (let i of nodes) {
    dist[i] = {};
    next[i] = {};
    for (let j of nodes) {
      if (i === j) dist[i][j] = 0;
      else if (graph[i][j] !== undefined) dist[i][j] = graph[i][j];
      else dist[i][j] = Infinity;
      if (graph[i][j] !== undefined) next[i][j] = j;
      else next[i][j] = null;
    }
  }

  for (let k of nodes) {
    for (let i of nodes) {
      for (let j of nodes) {
        if (dist[i][k] + dist[k][j] < dist[i][j]) {
          dist[i][j] = dist[i][k] + dist[k][j];
          next[i][j] = next[i][k];
        }
      }
    }
  }

  if (next[start][end] === null) return { path: [], distance: Infinity };
  const path = [];
  let u = start;
  while (u !== end) {
    path.push(u);
    u = next[u][end];
  }
  path.push(end);
  return { path, distance: dist[start][end] };
}

// A* Algorithm
function aStar(graph, start, end, coords) {
  const open = new Set([start]);
  const cameFrom = {};
  const gScore = {};
  const fScore = {};

  for (let node in graph) {
    gScore[node] = Infinity;
    fScore[node] = Infinity;
  }
  gScore[start] = 0;
  fScore[start] = heuristic(start, end, coords);

  while (open.size > 0) {
    let current = null;
    let bestF = Infinity;
    for (let n of open) {
      if (fScore[n] < bestF) {
        bestF = fScore[n];
        current = n;
      }
    }

    if (current === end) {
      const path = [];
      let c = current;
      while (c) {
        path.unshift(c);
        c = cameFrom[c];
      }
      return { path, distance: gScore[end] };
    }

    open.delete(current);
    for (let neighbor in graph[current]) {
      const tentative = gScore[current] + graph[current][neighbor];
      if (tentative < gScore[neighbor]) {
        cameFrom[neighbor] = current;
        gScore[neighbor] = tentative;
        fScore[neighbor] = tentative + heuristic(neighbor, end, coords);
        open.add(neighbor);
      }
    }
  }
  return { path: [], distance: Infinity };
}

function heuristic(a, b, coords) {
  const dx = coords[a].x - coords[b].x;
  const dy = coords[a].y - coords[b].y;
  return Math.sqrt(dx * dx + dy * dy);
}

// --------------------------------------------
//  Main Component
// --------------------------------------------
export default function App() {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [result, setResult] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [algorithm, setAlgorithm] = useState("dijkstra");

  const graph = {
    A: { B: 4, C: 2 },
    B: { A: 4, C: 5, D: 10 },
    C: { A: 2, B: 5, D: 3, E: 4 },
    D: { B: 10, C: 3, E: 6, F: 2 },
    E: { C: 4, D: 6, F: 1 },
    F: { D: 2, E: 1 },
  };

  const coords = {
    A: { x: 0, y: 0 },
    B: { x: 2, y: 1 },
    C: { x: 1, y: 2 },
    D: { x: 3, y: 3 },
    E: { x: 4, y: 2 },
    F: { x: 5, y: 1 },
  };

  function handleFind() {
    if (!source || !destination) return alert("Enter both cities A–F.");

    let data;
    if (algorithm === "dijkstra") data = dijkstra(graph, source, destination);
    else if (algorithm === "bellman") data = bellmanFord(graph, source, destination);
    else if (algorithm === "floyd") data = floydWarshall(graph, source, destination);
    else if (algorithm === "astar") data = aStar(graph, source, destination, coords);

    if (data) {
      data.algorithm = algorithm;
      setResult(data);
      setShowMap(true);
    }
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-slate-900 to-blue-950 text-white overflow-hidden">
      <AnimatePresence mode="wait">
        {!showMap ? (
          <motion.div
            key="form"
            className="text-center space-y-6 p-10 bg-slate-800/60 rounded-2xl shadow-2xl border border-slate-700 backdrop-blur-sm"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.6 }}
          >
            <h1 className="text-4xl font-bold mb-6 text-blue-400">
              ✈️ Airplane Route Optimizer
            </h1>

            <div className="flex flex-col md:flex-row justify-center gap-4">
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Source (A–F)"
                value={source}
                onChange={(e) => setSource(e.target.value.toUpperCase())}
              />
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Destination (A–F)"
                value={destination}
                onChange={(e) => setDestination(e.target.value.toUpperCase())}
              />
            </div>

            <div className="flex justify-center">
              <select
                className="p-3 text-black rounded-xl outline-none"
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
              >
                <option value="dijkstra">Dijkstra</option>
                <option value="bellman">Bellman-Ford</option>
                <option value="floyd">Floyd–Warshall</option>
                <option value="astar">A*</option>
              </select>
            </div>

            <button
              onClick={handleFind}
              className="bg-blue-500 hover:bg-blue-600 px-8 py-3 rounded-xl text-lg font-semibold transition transform hover:scale-105 shadow-lg shadow-blue-700/50"
            >
              Find Route
            </button>

            {result && <StatsCard result={result} />}
          </motion.div>
        ) : (
          <motion.div
            key="map"
            className="relative w-full h-screen"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
            <Visualizer
            path={result.path}
            distance={result.distance}
            algorithm={result.algorithm}
            />

            <div className="absolute top-6 right-6">
              <button
                onClick={() => setShowMap(false)}
                className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-xl shadow-lg"
              >
                ← Back
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
