import React from "react";
import { motion } from "framer-motion";

const algoColors = {
  dijkstra: "#60A5FA",
  bellman: "#34D399",
  floyd: "#F87171",
  "floyd–warshall": "#F87171",
  "a*": "#FACC15",
  johnson: "#C084FC",
};

export default function Visualizer({ path, distance, algorithm, all }) {
  // Fixed coordinate layout (visible and consistent)
  const coords = {
    A: { x: 100, y: 500 },
    B: { x: 250, y: 450 },
    C: { x: 180, y: 320 },
    D: { x: 300, y: 250 },
    E: { x: 350, y: 460 },
    F: { x: 420, y: 380 },
    G: { x: 350, y: 230 },
    H: { x: 480, y: 300 },
    I: { x: 600, y: 260 },
    J: { x: 550, y: 470 },
    K: { x: 650, y: 400 },
    L: { x: 530, y: 180 },
    M: { x: 640, y: 290 },
    N: { x: 720, y: 210 },
    O: { x: 700, y: 480 },
    P: { x: 800, y: 350 },
    Q: { x: 850, y: 250 },
  };

  // helper for drawing lines safely
  const Segment = ({ from, to, color }) => {
    const a = coords[from];
    const b = coords[to];
    if (!a || !b) return null;

    const dx = b.x - a.x;
    const dy = b.y - a.y;
    const len = Math.sqrt(dx * dx + dy * dy);
    const angle = Math.atan2(dy, dx);

    return (
      <motion.div
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ duration: 0.4 }}
        style={{
          position: "absolute",
          left: a.x,
          top: a.y,
          width: len,
          height: 3,
          transformOrigin: "0% 50%",
          transform: `rotate(${angle}rad)`,
          background: color,
          borderRadius: 3,
          boxShadow: `0 0 6px ${color}`,
          pointerEvents: "none",
        }}
      />
    );
  };

  // --- Single Algorithm Mode ---
  if (algorithm !== "compare") {
    if (!Array.isArray(path) || path.length < 2)
      return (
        <div className="flex items-center justify-center h-screen text-xl text-white">
          No valid route found.
        </div>
      );

    const color = algoColors[algorithm] || "#60A5FA";

    // filter invalid node names
    const cleanPath = path.filter((n) => coords[n]);

    return (
      <div className="relative w-full h-screen bg-slate-900">
        {cleanPath.map(
          (node, i) =>
            i < cleanPath.length - 1 && (
              <Segment key={i} from={node} to={cleanPath[i + 1]} color={color} />
            )
        )}

        {/* city markers */}
        {Object.entries(coords).map(([name, pos]) => (
          <div
            key={name}
            className="absolute flex items-center justify-center w-6 h-6 text-xs font-semibold text-black bg-yellow-300 rounded-full"
            style={{ left: pos.x - 8, top: pos.y - 8 }}
          >
            {name}
          </div>
        ))}

        {/* info box */}
        <div className="absolute bottom-6 left-1/2 -translate-x-1/2 bg-slate-800/80 px-6 py-3 rounded-xl text-center text-white shadow-lg border border-slate-700">
          <div className="text-lg font-semibold">
            Algorithm:{" "}
            <span className="text-blue-400 capitalize">{algorithm}</span>
          </div>
          <div>
            Total Distance:{" "}
            <span className="font-bold text-green-400">{distance}</span>
          </div>
          <div>Path: {cleanPath.join(" → ")}</div>
        </div>
      </div>
    );
  }

  // --- Compare All Mode ---
  return (
    <div className="relative w-full h-screen bg-slate-900">
      {all?.map((a, i) => {
        const clr = a.colorHex || algoColors[a.name?.toLowerCase()] || "#888";
        const clean = (a.data?.path || []).filter((n) => coords[n]);
        if (clean.length < 2) return null;

        return clean.map(
          (n, j) =>
            j < clean.length - 1 && (
              <Segment key={`${i}-${j}`} from={n} to={clean[j + 1]} color={clr} />
            )
        );
      })}

      {/* City labels */}
      {Object.entries(coords).map(([name, pos]) => (
        <div
          key={name}
          className="absolute text-xs text-white"
          style={{ left: pos.x - 5, top: pos.y - 5 }}
        >
          {name}
        </div>
      ))}

      {/* Summary box */}
      <div className="absolute bottom-6 left-1/2 -translate-x-1/2 bg-slate-800/90 p-6 rounded-xl shadow-xl border border-slate-700 max-w-xl w-[90%]">
        <h2 className="text-xl font-bold mb-2 text-blue-400 text-center">
          ✈️ Algorithm Comparison Summary
        </h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 gap-2 text-sm">
          {all?.map((a, i) => (
            <div
              key={i}
              className="bg-slate-700/50 p-2 rounded-lg text-center border border-slate-600"
              style={{ borderTop: `3px solid ${a.colorHex}` }}
            >
              <div className="font-semibold text-white mb-1">{a.name}</div>
              <div className="text-green-400">
                Dist: {a.data?.distance ?? "–"}
              </div>
              <div className="text-xs text-gray-300 truncate">
                {a.data?.path?.join(" → ") || "No Path"}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
