import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

/**
 * Visualizer.js
 * Props:
 *  - path: array of node ids for single-algo mode (e.g. ['A','C','H','M','Q'])
 *  - distance: numeric total distance for the path
 *  - algorithm: string (e.g. "dijkstra" or "compare")
 *  - all: when algorithm === "compare", this should be an array:
 *         [{ name: "Dijkstra", color: "blue", data: { path: [...], distance } }, ...]
 */

const COLOR_MAP = {
  blue: "#60A5FA",
  green: "#4ADE80",
  red: "#F87171",
  yellow: "#FACC15",
  purple: "#C084FC",
  gray: "#cbd5e1",
};

export default function Visualizer({ path = [], distance = 0, algorithm = "", all = [] }) {
  const [dimensions, setDimensions] = useState({
    width: typeof window !== "undefined" ? window.innerWidth : 1200,
    height: typeof window !== "undefined" ? window.innerHeight : 800,
  });

  useEffect(() => {
    const onResize = () => setDimensions({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  const { width, height } = dimensions;

  // ---------------------------
  // City coords (percentages)
  // tuned to land positions on usual world-map png
  // A..Q inclusive (3 points in India: K,L,M)
  // ---------------------------
  const cityCoords = {
    A: { x: 12, y: 45 }, // West / USA
    B: { x: 20, y: 35 },
    C: { x: 26, y: 48 },
    D: { x: 34, y: 30 },
    E: { x: 28, y: 70 },
    F: { x: 40, y: 78 },
    G: { x: 45, y: 40 },
    H: { x: 50, y: 50 },
    I: { x: 58, y: 35 },
    J: { x: 62, y: 30 },
    // India cluster
    K: { x: 73, y: 38 },
    L: { x: 76, y: 46 },
    M: { x: 78, y: 55 },
    N: { x: 81, y: 33 },
    O: { x: 84, y: 60 },
    P: { x: 88, y: 50 },
    Q: { x: 92, y: 45 },
  };

  // convert to pixel positions
  const scaled = {};
  Object.keys(cityCoords).forEach((c) => {
    scaled[c] = {
      x: (cityCoords[c].x / 100) * width,
      y: (cityCoords[c].y / 100) * height,
    };
  });

  // ---------------------------
  // helpers: draw segments for a path
  // returns array of JSX motion.divs
  // ---------------------------
  const drawSegmentsForPath = (p = [], colorHex = COLOR_MAP.yellow, delayBase = 0) => {
    const segs = [];
    if (!Array.isArray(p) || p.length < 2) return segs;

    for (let i = 0; i < p.length - 1; i++) {
      const a = scaled[p[i]];
      const b = scaled[p[i + 1]];
      if (!a || !b) continue;
      const dx = b.x - a.x;
      const dy = b.y - a.y;
      const len = Math.hypot(dx, dy);
      const angle = Math.atan2(dy, dx);

      segs.push(
        <motion.div
          key={`seg-${p[i]}-${p[i + 1]}-${i}-${colorHex}`}
          initial={{ scaleX: 0 }}
          animate={{ scaleX: 1 }}
          transition={{ duration: 0.9, delay: delayBase + i * 0.18 }}
          style={{
            position: "absolute",
            left: a.x,
            top: a.y,
            width: len,
            height: 3,
            transformOrigin: "0% 50%",
            transform: `rotate(${angle}rad) translateY(-50%)`,
            background: colorHex,
            boxShadow: `0 0 12px ${colorHex}66`,
          }}
        />
      );
    }
    return segs;
  };

  // ---------------------------
  // Single-path precomputation (for animated plane)
  // ---------------------------
  const singleSegments = [];
  if (Array.isArray(path) && path.length > 1) {
    for (let i = 0; i < path.length - 1; i++) {
      const a = scaled[path[i]];
      const b = scaled[path[i + 1]];
      if (!a || !b) continue;
      singleSegments.push([a, b]);
    }
  }

  const planeStart = singleSegments.length > 0 ? singleSegments[0][0] : null;
  const planeXs = Array.isArray(path) ? path.map((c) => (scaled[c] ? scaled[c].x : 0)) : [];
  const planeYs = Array.isArray(path) ? path.map((c) => (scaled[c] ? scaled[c].y : 0)) : [];

  // ---------------------------
  // Render
  // ---------------------------
  return (
    <div
      className="relative w-full h-screen overflow-hidden"
      style={{
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* City markers - keep them on top visually */}
      {Object.entries(scaled).map(([city, pos]) => (
        <div
          key={`city-${city}`}
          style={{
            position: "absolute",
            left: pos.x,
            top: pos.y,
            transform: "translate(-50%,-50%)",
            zIndex: 50,
            pointerEvents: "auto",
          }}
        >
          <div
            style={{
              width: 28,
              height: 28,
              borderRadius: 999,
              background: "#3B82F6",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "white",
              fontWeight: 700,
              fontSize: 12,
              boxShadow: "0 6px 18px rgba(59,130,246,0.18)",
            }}
          >
            {city}
          </div>
        </div>
      ))}

      {/* Draw single or multi (compare) routes */}
      {algorithm === "compare" && Array.isArray(all) && all.length > 0 ? (
        // compare mode: draw each algorithm route in its color
        all.map((algo, idx) => {
          const colorName = (algo.color || "yellow-400").split("-")[0]; // color token used earlier
          const colorHex = COLOR_MAP[colorName] || COLOR_MAP.gray;
          const p = algo.data?.path || [];
          return (
            <React.Fragment key={`compare-${idx}`}>
              {drawSegmentsForPath(p, colorHex, idx * 0.28)}
            </React.Fragment>
          );
        })
      ) : (
        // single algorithm mode
        drawSegmentsForPath(path, COLOR_MAP.yellow, 0)
      )}

      {/* Animated plane (single mode) */}
      {algorithm !== "compare" && planeStart && planeXs.length > 0 && planeYs.length > 0 && (
        <motion.div
          initial={{ x: planeStart.x, y: planeStart.y }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(4, Math.min(14, path.length * 1.1)),
            ease: "easeInOut",
            times: path.map((_, i) => (path.length === 1 ? 0 : i / (path.length - 1))),
          }}
          style={{
            position: "absolute",
            left: 0,
            top: 0,
            translate: "-50% -50%",
            zIndex: 60,
            pointerEvents: "none",
          }}
        >
          <div style={{ fontSize: 34, textShadow: "0 0 12px rgba(255,75,75,0.9)" }}>✈️</div>
        </motion.div>
      )}

      {/* Route summary box (bottom-left) */}
      {((algorithm !== "compare" && Array.isArray(path) && path.length > 0) ||
        (algorithm === "compare" && Array.isArray(all) && all.length > 0)) && (
        <div
          style={{
            position: "absolute",
            left: 18,
            bottom: 18,
            zIndex: 70,
            minWidth: 260,
            background: "rgba(15,23,42,0.78)",
            color: "#fff",
            padding: 14,
            borderRadius: 12,
            backdropFilter: "blur(6px)",
            border: "1px solid rgba(148,163,184,0.06)",
            boxShadow: "0 8px 30px rgba(2,6,23,0.6)",
            fontSize: 13,
          }}
        >
          <div style={{ marginBottom: 8, color: "#FACC15", fontWeight: 700 }}>Algorithm Legend</div>

          {algorithm === "compare" ? (
            // list each algo with color + path
            all.map((a, i) => {
              const name = a.name || `Algo ${i + 1}`;
              const colorName = (a.color || "yellow-400").split("-")[0];
              const colorHex = COLOR_MAP[colorName] || COLOR_MAP.gray;
              return (
                <div key={`leg-${i}`} style={{ display: "flex", gap: 10, alignItems: "center", marginBottom: 6 }}>
                  <div style={{ width: 12, height: 12, borderRadius: 6, background: colorHex, boxShadow: `0 0 8px ${colorHex}66` }} />
                  <div style={{ color: "#E6EEF8" }}>
                    <div style={{ fontWeight: 600 }}>{name}</div>
                    <div style={{ fontSize: 12, color: "#A8C1D8", marginTop: 2 }}>
                      {a.data?.path?.length ? a.data.path.join(" → ") : "No path"}
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            // single mode: show algorithm name, path and distance
            <div>
              <div style={{ color: "#E6EEF8", fontWeight: 700, marginBottom: 6 }}>
                {algorithm ? algorithm.toUpperCase() : "RESULT"}
              </div>
              <div style={{ fontSize: 12, color: "#A8C1D8", marginBottom: 6 }}>
                Path: {Array.isArray(path) && path.length ? path.join(" → ") : "—"}
              </div>
              <div style={{ fontSize: 12, color: "#A8C1D8" }}>Distance: {distance === Infinity ? "∞ (unreachable)" : distance}</div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
