import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import map from "./assets/map.png";

export default function Visualizer({ path = [], distance = 0, algorithm = "", all = [] }) {
  const [dimensions, setDimensions] = useState({
    width: typeof window !== "undefined" ? window.innerWidth : 1200,
    height: typeof window !== "undefined" ? window.innerHeight : 800,
  });

  useEffect(() => {
    const onResize = () => setDimensions({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  const { width, height } = dimensions;

  // -----------------------------
  // City coordinates (on land)
  // 15 points total (A–O)
  // with 3 in India (K, L, M)
  // -----------------------------
  const cityCoords = {
    A: { x: 10, y: 45 }, // USA
    B: { x: 18, y: 40 }, // Mexico
    C: { x: 25, y: 35 }, // Brazil
    D: { x: 30, y: 55 }, // Africa
    E: { x: 40, y: 40 }, // Europe
    F: { x: 45, y: 25 }, // Norway
    G: { x: 52, y: 50 }, // Middle East
    H: { x: 60, y: 40 }, // Turkey
    I: { x: 65, y: 30 }, // Central Asia
    J: { x: 70, y: 35 }, // Pakistan
    K: { x: 75, y: 38 }, // India (Delhi)
    L: { x: 77, y: 48 }, // India (Mumbai)
    M: { x: 79, y: 58 }, // India (Chennai)
    N: { x: 83, y: 40 }, // Thailand
    O: { x: 90, y: 45 }, // China
    P: { x: 93, y: 52 }, // Japan
    Q: { x: 96, y: 35 }, // Korea
  };

  // Convert to pixels
  const scaled = {};
  Object.keys(cityCoords).forEach((city) => {
    scaled[city] = {
      x: (cityCoords[city].x / 100) * width,
      y: (cityCoords[city].y / 100) * height,
    };
  });

  // Colors for algorithms
  const colors = {
    Dijkstra: "#facc15",
    "Bellman-Ford": "#22c55e",
    "Floyd-Warshall": "#3b82f6",
    "A*": "#ef4444",
    Johnson: "#a855f7",
  };

  // -----------------------------
  // Draw line segments
  // -----------------------------
  const drawLine = (from, to, color, delay = 0) => {
    const a = scaled[from];
    const b = scaled[to];
    if (!a || !b) return null;
    const dx = b.x - a.x;
    const dy = b.y - a.y;
    const len = Math.sqrt(dx * dx + dy * dy);
    const angle = Math.atan2(dy, dx);
    return (
      <motion.div
        key={`${from}-${to}-${color}`}
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ duration: 1, delay }}
        style={{
          position: "absolute",
          left: a.x,
          top: a.y,
          width: len,
          height: 3,
          transform: `rotate(${angle}rad)`,
          transformOrigin: "0 0",
          background: color,
          boxShadow: `0 0 8px ${color}`,
          zIndex: 5,
        }}
      />
    );
  };

  // -----------------------------
  // Create path lines
  // -----------------------------
  const renderPath = (p, color, delayBase = 0) => {
    const lines = [];
    for (let i = 0; i < p.length - 1; i++) {
      lines.push(drawLine(p[i], p[i + 1], color, delayBase + i * 0.2));
    }
    return lines;
  };

  // -----------------------------
  // Plane animation (only single algo)
  // -----------------------------
  const singleSegments = [];
  if (Array.isArray(path) && path.length > 1) {
    for (let i = 0; i < path.length - 1; i++) {
      const a = scaled[path[i]];
      const b = scaled[path[i + 1]];
      if (a && b) singleSegments.push([a, b]);
    }
  }

  const planeXs = singleSegments.map((seg) => seg[0].x);
  const planeYs = singleSegments.map((seg) => seg[0].y);
  if (singleSegments.length > 0) {
    planeXs.push(singleSegments[singleSegments.length - 1][1].x);
    planeYs.push(singleSegments[singleSegments.length - 1][1].y);
  }

  // -----------------------------
  // UI Render
  // -----------------------------
  return (
    <div
      className="relative w-full h-screen overflow-hidden"
      style={{
        backgroundImage: `url(${map})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* Draw city dots */}
      {Object.keys(scaled).map((city) => (
        <div
          key={city}
          style={{
            position: "absolute",
            left: scaled[city].x,
            top: scaled[city].y,
            transform: "translate(-50%, -50%)",
            zIndex: 20,
          }}
        >
          <div
            style={{
              width: 28,
              height: 28,
              borderRadius: "50%",
              backgroundColor: "#2563eb",
              color: "white",
              fontWeight: "bold",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              fontSize: 12,
              boxShadow: "0 0 12px #2563eb",
            }}
          >
            {city}
          </div>
        </div>
      ))}

      {/* Compare mode lines */}
      {algorithm === "compare" && all.length > 0
        ? all.map((algo, idx) => {
            const clr = colors[algo.name] || "#fff";
            return renderPath(algo.data.path || [], clr, idx * 0.5);
          })
        : renderPath(path, colors[algorithm] || "#facc15", 0)}

      {/* Plane animation */}
      {algorithm !== "compare" && planeXs.length > 0 && planeYs.length > 0 && (
        <motion.div
          initial={{ x: planeXs[0], y: planeYs[0] }}
          animate={{ x: planeXs, y: planeYs }}
          transition={{
            duration: Math.max(5, path.length * 1.2),
            ease: "easeInOut",
            times: path.map((_, i) => (path.length === 1 ? 0 : i / (path.length - 1))),
          }}
          style={{
            position: "absolute",
            left: 0,
            top: 0,
            translate: "-50% -50%",
            zIndex: 30,
          }}
        >
          <div style={{ fontSize: 30, textShadow: "0 0 10px #fff" }}>✈️</div>
        </motion.div>
      )}

      {/* Route Summary */}
      {((algorithm !== "compare" && path.length > 0) ||
        (algorithm === "compare" && all.length > 0)) && (
        <div
          style={{
            position: "absolute",
            bottom: 20,
            left: 20,
            backgroundColor: "rgba(0,0,0,0.6)",
            color: "#fff",
            padding: "14px 18px",
            borderRadius: 12,
            backdropFilter: "blur(4px)",
            maxWidth: 320,
            fontSize: 13,
            zIndex: 50,
          }}
        >
          {algorithm === "compare" ? (
            <>
              <h3 style={{ marginBottom: 6, fontWeight: "bold", color: "#facc15" }}>Algorithm Comparison</h3>
              {all.map((a, i) => (
                <div key={i} style={{ marginBottom: 8 }}>
                  <span
                    style={{
                      background: colors[a.name] || "#fff",
                      width: 12,
                      height: 12,
                      display: "inline-block",
                      borderRadius: "50%",
                      marginRight: 8,
                    }}
                  ></span>
                  <b>{a.name}:</b>{" "}
                  {a.data.path && a.data.path.length > 0
                    ? a.data.path.join(" → ")
                    : "No valid path"}
                </div>
              ))}
            </>
          ) : (
            <>
              <h3 style={{ marginBottom: 6, fontWeight: "bold", color: "#facc15" }}>
                {algorithm || "Result"}
              </h3>
              <div>Path: {path.length ? path.join(" → ") : "No path"}</div>
              <div>Distance: {distance === Infinity ? "∞" : distance}</div>
            </>
          )}
        </div>
      )}
    </div>
  );
}
