function App() {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [result, setResult] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [algorithm, setAlgorithm] = useState("dijkstra"); // default to dijkstra

  // Graph (unchanged)
  const graph = {
    A: { B: 4, C: 2 },
    B: { A: 4, C: 5, D: 10 },
    C: { A: 2, B: 5, D: 3, E: 4 },
    D: { B: 10, C: 3, E: 6, F: 2 },
    E: { C: 4, D: 6, F: 1 },
    F: { D: 2, E: 1 },
  };

  // ---------- Helper: reconstruct path from "previous" map ----------
  const reconstructPath = (previous, start, end) => {
    if (!previous || previous[end] === undefined) return [];
    const path = [];
    let cur = end;
    // If unreachable, previous[cur] may be null/undefined and distances[end] Infinity handled separately
    while (cur !== null && cur !== undefined) {
      path.unshift(cur);
      if (cur === start) break;
      cur = previous[cur];
    }
    if (path[0] !== start) return []; // no path
    return path;
  };

  // ---------- Dijkstra ----------
  const dijkstra = (g, start, end) => {
    const distances = {};
    const visited = {};
    const previous = {};

    Object.keys(g).forEach((v) => {
      distances[v] = Infinity;
      previous[v] = null;
      visited[v] = false;
    });
    distances[start] = 0;

    const vertices = Object.keys(g);

    for (let i = 0; i < vertices.length; i++) {
      // pick unvisited node with smallest distance
      let current = null;
      let minDist = Infinity;
      for (let v of vertices) {
        if (!visited[v] && distances[v] < minDist) {
          minDist = distances[v];
          current = v;
        }
      }
      if (current === null) break; // remaining unreachable

      visited[current] = true;

      for (let neighbor in g[current]) {
        const alt = distances[current] + g[current][neighbor];
        if (alt < distances[neighbor]) {
          distances[neighbor] = alt;
          previous[neighbor] = current;
        }
      }
    }

    const path = reconstructPath(previous, start, end);
    const distance = distances[end] === Infinity ? Infinity : distances[end];
    return { path, distance, algorithm: "Dijkstra" };
  };

  // ---------- Bellman-Ford ----------
  const bellmanFord = (g, start, end) => {
    const vertices = Object.keys(g);
    const edges = [];
    vertices.forEach((u) => {
      Object.keys(g[u]).forEach((v) => {
        edges.push([u, v, g[u][v]]);
      });
    });

    const dist = {};
    const prev = {};
    vertices.forEach((v) => {
      dist[v] = Infinity;
      prev[v] = null;
    });
    dist[start] = 0;

    // relax edges |V|-1 times
    for (let i = 0; i < vertices.length - 1; i++) {
      let updated = false;
      for (let [u, v, w] of edges) {
        if (dist[u] !== Infinity && dist[u] + w < dist[v]) {
          dist[v] = dist[u] + w;
          prev[v] = u;
          updated = true;
        }
      }
      if (!updated) break;
    }

    // (We could check for negative cycles here; graph has positive weights so skip.)
    const path = reconstructPath(prev, start, end);
    const distance = dist[end] === Infinity ? Infinity : dist[end];
    return { path, distance, algorithm: "Bellman–Ford" };
  };

  // ---------- Floyd-Warshall (all-pairs) ----------
  const floydWarshall = (g, start, end) => {
    const vertices = Object.keys(g);
    const dist = {};
    const next = {};

    // init
    vertices.forEach((i) => {
      dist[i] = {};
      next[i] = {};
      vertices.forEach((j) => {
        if (i === j) dist[i][j] = 0;
        else if (g[i] && g[i][j] !== undefined) dist[i][j] = g[i][j];
        else dist[i][j] = Infinity;

        if (g[i] && g[i][j] !== undefined) next[i][j] = j;
        else next[i][j] = null;
      });
    });

    // main triple loop
    vertices.forEach((k) => {
      vertices.forEach((i) => {
        vertices.forEach((j) => {
          if (dist[i][k] + dist[k][j] < dist[i][j]) {
            dist[i][j] = dist[i][k] + dist[k][j];
            next[i][j] = next[i][k];
          }
        });
      });
    });

    // reconstruct path using next
    if (next[start][end] === null) {
      return { path: [], distance: Infinity, algorithm: "Floyd–Warshall" };
    }
    const path = [];
    let u = start;
    while (u !== end) {
      path.push(u);
      u = next[u][end];
      if (u === null) return { path: [], distance: Infinity, algorithm: "Floyd–Warshall" };
    }
    path.push(end);

    const distance = dist[start][end] === Infinity ? Infinity : dist[start][end];
    return { path, distance, algorithm: "Floyd–Warshall" };
  };

  // ---------- main handler ----------
  const findShortestPath = () => {
    if (!source || !destination) {
      alert("Please enter both source and destination cities (A–F).");
      return;
    }
    if (!graph[source] || !graph[destination]) {
      alert("Source or destination is invalid. Use cities A–F.");
      return;
    }
    if (!algorithm) {
      alert("Please select an algorithm.");
      return;
    }

    let data = null;
    switch (algorithm) {
      case "dijkstra":
        data = dijkstra(graph, source, destination);
        break;
      case "bellman-ford":
        data = bellmanFord(graph, source, destination);
        break;
      case "floyd-warshall":
        data = floydWarshall(graph, source, destination);
        break;
      default:
        alert("Selected algorithm not implemented.");
        return;
    }

    // If path empty or distance Infinity, alert (optional) — still show map so teacher sees no path case
    if (!data.path || data.path.length === 0 || data.distance === Infinity) {
      // still set result so visualizer can show nothing or highlight unreachable
      setResult(data);
      setShowMap(true);
      return;
    }

    setResult(data);
    setShowMap(true);
  };

  const resetView = () => {
    setShowMap(false);
    setResult(null);
    setSource("");
    setDestination("");
    // keep algorithm selection
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-slate-900 to-blue-950 text-white overflow-hidden">
      <AnimatePresence mode="wait">
        {!showMap ? (
          // ===== FORM SCREEN =====
          <motion.div
            key="form"
            className="text-center space-y-6 p-10 bg-slate-800/60 rounded-2xl shadow-2xl border border-slate-700 backdrop-blur-sm"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.6 }}
          >
            <h1 className="text-4xl font-bold mb-6 text-blue-400">
              ✈️ Airplane Route Optimizer
            </h1>

            <div className="flex flex-col md:flex-row justify-center gap-4">
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Enter Source City (A-F)"
                value={source}
                onChange={(e) => setSource(e.target.value.toUpperCase())}
              />
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Enter Destination City (A-F)"
                value={destination}
                onChange={(e) => setDestination(e.target.value.toUpperCase())}
              />
            </div>

            {/* Algorithm selector (small & unobtrusive) */}
            <div className="flex justify-center">
              <select
                className="p-3 text-black rounded-xl outline-none"
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
              >
                <option value="dijkstra">Dijkstra</option>
                <option value="bellman-ford">Bellman-Ford</option>
                <option value="floyd-warshall">Floyd-Warshall</option>
              </select>
            </div>

            <button
              onClick={findShortestPath}
              className="bg-blue-500 hover:bg-blue-600 px-8 py-3 rounded-xl text-lg font-semibold transition transform hover:scale-105 shadow-lg shadow-blue-700/50"
            >
              Find Route
            </button>

            {result && <StatsCard result={result} />}
          </motion.div>
        ) : (
          // ===== MAP SCREEN =====
          <motion.div
            key="map"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
            className="relative w-full h-screen"
          >
            {/* Route Map Visualizer */}
            <Visualizer path={result?.path || []} />

            {/* Route Summary Card (top-left corner) */}
            <div className="absolute top-6 left-6 bg-slate-800/70 border border-slate-700 rounded-xl p-4 shadow-lg text-sm backdrop-blur-md">
              <h2 className="font-semibold text-blue-400 mb-2">Route Summary</h2>
              <p className="text-xs mb-1">Algorithm: {result?.algorithm || "—"}</p>
              <p>Path: {result?.path && result.path.length ? result.path.join(" → ") : "No path"}</p>
              <p>Distance: {result?.distance === Infinity ? "∞ (unreachable)" : result?.distance}</p>
            </div>

            {/* Go Back Button */}
            <button
              onClick={resetView}
              className="absolute top-6 right-6 bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-xl shadow-lg"
            >
              ← Go Back
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
