import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import Visualizer from "./Visualizer";
import StatsCard from "./StatsCard";
import {
  dijkstra,
  bellmanFord,
  floydWarshall,
  aStar,
  johnson,
} from "./algorithms";

export default function App() {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [result, setResult] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [algorithm, setAlgorithm] = useState("dijkstra");

  // ------------------ GRAPH DATA ------------------
  const graph = {
  A: { B: 4, C: 8 },
  B: { A: 4, D: 7, E: 3 },
  C: { A: 8, F: 9, G: 5 },
  D: { B: 7, H: 2, I: 8 },
  E: { B: 3, I: 7, J: 5 },
  F: { C: 9, G: 2, K: 4 },
  G: { C: 5, F: 2, L: 6 },
  H: { D: 2, I: 3, M: 8 },
  I: { D: 8, E: 7, H: 3, N: 6 },
  J: { E: 5, N: 4, O: 7 },
  K: { F: 4, L: 3, P: 5 },
  L: { G: 6, K: 3, P: 2 },
  M: { H: 8, N: 5, Q: 7 },
  N: { I: 6, J: 4, M: 5, Q: 3 },
  O: { J: 7, Q: 2, P: 6 },
  P: { K: 5, L: 2, O: 6, Q: 4 },
  Q: { M: 7, N: 3, O: 2, P: 4 }
};


  // for A* heuristic distance calculation
  const coords = {
    A: { x: 0, y: 0 },
    B: { x: 2, y: 1 },
    C: { x: 1, y: 2 },
    D: { x: 3, y: 3 },
    E: { x: 4, y: 2 },
    F: { x: 5, y: 1 },
  };

  // ------------------ MAIN HANDLER ------------------
  function handleFind() {
    if (!source || !destination) {
      alert("⚠️ Please enter both source and destination (A–F).");
      return;
    }

    let data = null;

    switch (algorithm) {
      case "dijkstra":
        data = dijkstra(graph, source, destination);
        break;
      case "bellman":
        data = bellmanFord(graph, source, destination);
        break;
      case "floyd":
        data = floydWarshall(graph, source, destination);
        break;
      case "astar":
        data = aStar(graph, source, destination, coords);
        break;
      case "johnson":
        data = johnson(graph, source, destination);
        break;
      default:
        data = dijkstra(graph, source, destination);
    }

    if (data) {
      data.algorithm = algorithm;
      setResult(data);
      setShowMap(true);
    }
  }

  // ------------------ UI ------------------
  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-slate-900 to-blue-950 text-white overflow-hidden">
      <AnimatePresence mode="wait">
        {!showMap ? (
          // ====== INPUT FORM ======
          <motion.div
            key="form"
            className="text-center space-y-6 p-10 bg-slate-800/60 rounded-2xl shadow-2xl border border-slate-700 backdrop-blur-sm"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.6 }}
          >
            <h1 className="text-4xl font-bold mb-6 text-blue-400">
              ✈️ Airplane Route Optimizer
            </h1>

            {/* Source and Destination Inputs */}
            <div className="flex flex-col md:flex-row justify-center gap-4">
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Source (A–F)"
                value={source}
                onChange={(e) => setSource(e.target.value.toUpperCase())}
              />
              <input
                className="p-3 text-black rounded-xl outline-none"
                placeholder="Destination (A–F)"
                value={destination}
                onChange={(e) => setDestination(e.target.value.toUpperCase())}
              />
            </div>

            {/* Algorithm Dropdown */}
            <div className="flex justify-center">
              <select
                className="p-3 text-black rounded-xl outline-none"
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
              >
                <option value="dijkstra">Dijkstra</option>
                <option value="bellman">Bellman-Ford</option>
                <option value="floyd">Floyd–Warshall</option>
                <option value="astar">A*</option>
                <option value="johnson">Johnson’s Algorithm</option>
              </select>
            </div>

            {/* Button */}
            <button
              onClick={handleFind}
              className="bg-blue-500 hover:bg-blue-600 px-8 py-3 rounded-xl text-lg font-semibold transition transform hover:scale-105 shadow-lg shadow-blue-700/50"
            >
              Find Route
            </button>

            {/* Optional Stats Summary */}
            {result && <StatsCard result={result} />}
          </motion.div>
        ) : (
          // ====== MAP VISUALIZATION ======
          <motion.div
            key="map"
            className="relative w-full h-screen"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
            <Visualizer
              path={result.path}
              distance={result.distance}
              algorithm={result.algorithm}
            />

            {/* Back Button */}
            <div className="absolute top-6 right-6">
              <button
                onClick={() => setShowMap(false)}
                className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-xl shadow-lg"
              >
                ← Back
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
