import React from "react";

const algorithms = [
  "Dijkstra",
  "BFS",
  "DFS",
  "Kruskal",
  "Prim",
  "Bubble Sort",
  "Merge Sort",
  "Quick Sort",
];

export default function AlgorithmSelector({ selected, setSelected }) {
  return (
    <div className="p-4 bg-slate-800/60 rounded-xl border border-slate-700 backdrop-blur-sm">
      <h2 className="text-xl font-semibold text-blue-300 mb-3">Choose Algorithm</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
        {algorithms.map((algo) => (
          <button
            key={algo}
            onClick={() => setSelected(algo)}
            className={`px-4 py-2 rounded-lg font-medium transition-shadow ${
              selected === algo
                ? "bg-blue-500 shadow-lg text-white"
                : "bg-slate-700 hover:bg-slate-600 text-slate-100"
            }`}
          >
            {algo}
          </button>
        ))}
      </div>
      <div className="mt-3">
        <button
          onClick={() => setSelected("")}
          className="text-sm text-slate-300 underline"
        >
          Clear selection
        </button>
      </div>
    </div>
  );
}
